void OS_BSP_Lock_Impl(void)
{
    int status;

    /* Open a persistent log file for probes. This records control flow and state
     * so other agents can reproduce the reported dead-code scenario. */
    FILE *os_bsp_log = fopen("/tmp/logs/1538.txt", "a");
    if (os_bsp_log != NULL)
    {
        fprintf(os_bsp_log, "OS_BSP_Lock_Impl: entered\n");
        fflush(os_bsp_log);
    }

    status = pthread_mutex_lock(&OS_BSP_GenericLinuxGlobal.AccessMutex);

    if (status < 0)
    {
        /* Log the error and the branch taken. Note: BSP_DEBUG may be an empty
         * macro in some builds; the explicit file logging here ensures the
         * branch and state are observable to reproduce the violation scenario. */
        if (os_bsp_log != NULL)
        {
            /* strerror is used in the original code; log the returned message. */
            fprintf(os_bsp_log, "OS_BSP_Lock_Impl: pthread_mutex_lock failed: status=%d, strerror=%s\n",
                    status, strerror(status));
            fflush(os_bsp_log);
        }

        BSP_DEBUG("pthread_mutex_lock: %s\n", strerror(status));
    }
    else
    {
        if (os_bsp_log != NULL)
        {
            fprintf(os_bsp_log, "OS_BSP_Lock_Impl: pthread_mutex_lock succeeded, calling pthread_setcancelstate to disable cancellation\n");
            fflush(os_bsp_log);
        }

        /* Temporarily Disable/Defer thread cancellation.
         * Note that OS_BSP_ConsoleOutput_Impl() calls write() which is a cancellation point.
         * So if this calling task is canceled, it risks leaving the BSP locked.
         */
        pthread_setcancelstate(PTHREAD_CANCEL_DISABLE, &OS_BSP_GenericLinuxGlobal.AccessCancelState);
    }

    if (os_bsp_log != NULL)
    {
        fprintf(os_bsp_log, "OS_BSP_Lock_Impl: exiting with status=%d\n", status);
        fclose(os_bsp_log);
    }
}
