void OS_BSP_Unlock_Impl(void)
{
    int status;

    status = pthread_mutex_unlock(&OS_BSP_GenericLinuxGlobal.AccessMutex);

    /* Minimal probe: log entry, thread, mutex address and status to help reproduce the scenario. */
    {
        FILE *logf = fopen("/tmp/logs/98.txt", "a");
        if (logf != NULL)
        {
            /* pthread_t may be an opaque type; cast to unsigned long for logging portability. */
            fprintf(logf, "OS_BSP_Unlock_Impl: entered. thread=%lu, mutex_addr=%p, status=%d\n",
                    (unsigned long)pthread_self(), (void *)&OS_BSP_GenericLinuxGlobal.AccessMutex, status);
            if (status != 0)
            {
                /* Log the strerror output as additional state info. */
                fprintf(logf, "OS_BSP_Unlock_Impl: strerror(status)=%s\n", strerror(status));
            }
            fclose(logf);
        }
    }

    if (status < 0)
    {
        /* Preserve original behavior: call BSP_DEBUG (may expand to nothing). */
        BSP_DEBUG("pthread_mutex_unlock: %s\n", strerror(status));

        /* Also log that the error branch was taken so agents can observe the executed yet effectless BSP_DEBUG call. */
        {
            FILE *logf = fopen("/tmp/logs/98.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "OS_BSP_Unlock_Impl: error branch taken (status=%d). BSP_DEBUG executed (may expand to nothing).\n", status);
                fclose(logf);
            }
        }
    }
    else
    {
        /* Restore previous cancellability state */
        pthread_setcancelstate(OS_BSP_GenericLinuxGlobal.AccessCancelState, NULL);

        /* Log that cancel state was restored. */
        {
            FILE *logf = fopen("/tmp/logs/98.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "OS_BSP_Unlock_Impl: success branch taken. restored cancel state=%d\n", OS_BSP_GenericLinuxGlobal.AccessCancelState);
                fclose(logf);
            }
        }
    }
}
