int32 OS_BinSemGetInfo(osal_id_t sem_id, OS_bin_sem_prop_t *bin_prop)
{
    OS_common_record_t *record;
    OS_object_token_t   token;
    int32               return_code;
    FILE *log = NULL;

    /* Check parameters */
    OS_CHECK_POINTER(bin_prop);

    /* Open log after pointer check to avoid leaking a file handle if the macro returns early */
    log = fopen("/tmp/logs/3155.txt", "a");
    if (log)
    {
        fprintf(log, "OS_BinSemGetInfo ENTER: sem_id=%llu, bin_prop=%p\n", (unsigned long long)sem_id, (void *)bin_prop);
        fflush(log);
    }

    memset(bin_prop, 0, sizeof(OS_bin_sem_prop_t));
    if (log)
    {
        fprintf(log, "bin_prop zeroed\n");
        fflush(log);
    }

    /* Check Parameters */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, sem_id, &token);
    if (log)
    {
        fprintf(log, "OS_ObjectIdGetById returned %d, token_addr=%p\n", (int)return_code, (void *)&token);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_bin_sem_table, token);

        if (log)
        {
            fprintf(log, "record_addr=%p, name_entry=\"%s\", creator=%u\n",
                    (void *)record,
                    (record ? record->name_entry : "(null)"),
                    (unsigned int)(record ? record->creator : 0u));
            fflush(log);
        }

        strncpy(bin_prop->name, record->name_entry, sizeof(bin_prop->name) - 1);
        bin_prop->creator = record->creator;
        return_code       = OS_BinSemGetInfo_Impl(&token, bin_prop);

        if (log)
        {
            fprintf(log, "OS_BinSemGetInfo_Impl returned %d\n", (int)return_code);
            fflush(log);
        }

        OS_ObjectIdRelease(&token);

        if (log)
        {
            fprintf(log, "Released token at %p\n", (void *)&token);
            fclose(log);
            log = NULL;
        }
    }
    else
    {
        if (log)
        {
            /* Log the early-failure path and close the file */
            fprintf(log, "OS_BinSemGetInfo early return with code %d\n", (int)return_code);
            fclose(log);
            log = NULL;
        }
    }

    return return_code;
}
