int32 OS_BinSemGetInfo(osal_id_t sem_id, OS_bin_sem_prop_t *bin_prop)
{
    OS_common_record_t *record;
    OS_object_token_t   token;
    int32               return_code;

    /* Minimal entry probe: record that function was entered and parameters observed */
    {
        FILE *log = fopen("/tmp/logs/3155.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "ENTER OS_BinSemGetInfo: sem_id=%d, bin_prop=%p\n", (int)sem_id, (void *)bin_prop);
            fclose(log);
        }
    }

    /* Check parameters */
    OS_CHECK_POINTER(bin_prop);

    memset(bin_prop, 0, sizeof(OS_bin_sem_prop_t));

    /* Check Parameters */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, sem_id, &token);
    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_bin_sem_table, token);

        /* Log resolved record info before copying to bin_prop to capture state for debugging */
        {
            FILE *log = fopen("/tmp/logs/3155.txt", "a");
            if (log != NULL)
            {
                /* record, token address, name_entry and creator are useful to reproduce the scenario */
                fprintf(log, "OS_BinSemGetInfo: token_addr=%p, record=%p, record->name_entry=\"%s\", record->creator=%u\n",
                        (void *)&token, (void *)record,
                        (record != NULL && record->name_entry != NULL) ? record->name_entry : "(null)",
                        (unsigned int)record->creator);
                fclose(log);
            }
        }

        strncpy(bin_prop->name, record->name_entry, sizeof(bin_prop->name) - 1);
        bin_prop->creator = record->creator;
        return_code       = OS_BinSemGetInfo_Impl(&token, bin_prop);

        /* Log outcome after calling the implementation function */
        {
            FILE *log = fopen("/tmp/logs/3155.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "OS_BinSemGetInfo: after Impl return_code=%d, bin_prop->name=\"%s\", bin_prop->creator=%u\n",
                        (int)return_code,
                        bin_prop->name != NULL ? bin_prop->name : "(null)",
                        (unsigned int)bin_prop->creator);
                fclose(log);
            }
        }

        OS_ObjectIdRelease(&token);
    }
    else
    {
        /* Log failure to resolve object id */
        FILE *log = fopen("/tmp/logs/3155.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_BinSemGetInfo: OS_ObjectIdGetById failed return_code=%d\n", (int)return_code);
            fclose(log);
        }
    }

    return return_code;
}
