int32 OS_BinSemGetInfo(osal_id_t sem_id, OS_bin_sem_prop_t *bin_prop)
{
    OS_common_record_t *record;
    OS_object_token_t   token;
    int32               return_code;

    /* Open a log file to record control flow and state for debugging the pointer const-qualification scenario. */
    FILE *logf = fopen("/tmp/logs/3155.txt", "a");
    if (logf)
    {
        fprintf(logf, "OS_BinSemGetInfo entered: bin_prop=%p\n", (void *)bin_prop);
        fflush(logf);
    }

    /* Check parameters */
    OS_CHECK_POINTER(bin_prop);

    memset(bin_prop, 0, sizeof(OS_bin_sem_prop_t));
    if (logf)
    {
        fprintf(logf, "bin_prop zeroed\n");
        fflush(logf);
    }

    /* Check Parameters */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, sem_id, &token);
    if (logf)
    {
        fprintf(logf, "OS_ObjectIdGetById returned %d\n", (int)return_code);
        fflush(logf);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_bin_sem_table, token);

        if (logf)
        {
            fprintf(logf, "OS_OBJECT_TABLE_GET returned record=%p\n", (void *)record);
            if (record)
            {
                /* Log the fields that this function reads to demonstrate the pointer is only used for reading. */
                const char *name_entry = record->name_entry ? record->name_entry : "(null)";
                fprintf(logf, "record->name_entry=\"%s\"\n", name_entry);
                fprintf(logf, "record->creator=%u\n", (unsigned)record->creator);
            }
            else
            {
                fprintf(logf, "record is NULL\n");
            }
            fflush(logf);
        }

        /* Preserve original behavior: copy name and creator into bin_prop */
        strncpy(bin_prop->name, record->name_entry, sizeof(bin_prop->name) - 1);
        bin_prop->creator = record->creator;

        if (logf)
        {
            fprintf(logf, "Copied to bin_prop: name=\"%s\", creator=%u\n", bin_prop->name, (unsigned)bin_prop->creator);
            fflush(logf);
        }

        return_code = OS_BinSemGetInfo_Impl(&token, bin_prop);

        if (logf)
        {
            fprintf(logf, "OS_BinSemGetInfo_Impl returned %d\n", (int)return_code);
            fflush(logf);
        }

        OS_ObjectIdRelease(&token);

        if (logf)
        {
            fprintf(logf, "Released token\n");
            fflush(logf);
        }
    }

    if (logf)
    {
        fprintf(logf, "OS_BinSemGetInfo exiting with return_code=%d\n", (int)return_code);
        fclose(logf);
    }

    return return_code;
}
