int32 OS_BinSemGive(osal_id_t sem_id)
{
    OS_object_token_t token;
    int32             return_code;

    /* Logging probe: open log file (append) */
    FILE *f = fopen("/tmp/logs/255.txt", "a");

    /* storage for token bytes to detect whether the implementation modifies the object */
    unsigned int i;
    unsigned int n = (unsigned int)sizeof(token);
    unsigned char before_bytes[sizeof(token)];
    unsigned char after_bytes[sizeof(token)];

    if (f != NULL)
    {
        /* Log entry and parameter */
        fprintf(f, "OS_BinSemGive ENTER: sem_id=%ld\n", (long)sem_id);
        fflush(f);
    }

    /* Check Parameters */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_NONE, LOCAL_OBJID_TYPE, sem_id, &token);

    if (f != NULL)
    {
        fprintf(f, "OS_ObjectIdGetById returned %d\n", (int)return_code);
        /* Snapshot token bytes before calling implementation (to detect modifications) */
        for (i = 0; i < n; ++i)
        {
            before_bytes[i] = ((unsigned char *)&token)[i];
        }
        fprintf(f, "token before (%u bytes): ", n);
        for (i = 0; i < n; ++i)
        {
            fprintf(f, "%02x", before_bytes[i]);
        }
        fprintf(f, "\n");
        fflush(f);
    }

    if (return_code == OS_SUCCESS)
    {
        if (f != NULL)
        {
            fprintf(f, "Calling OS_BinSemGive_Impl with token address=%p\n", (void *)&token);
            fflush(f);
        }

        /* Call the original implementation as before */
        return_code = OS_BinSemGive_Impl(&token);

        if (f != NULL)
        {
            fprintf(f, "OS_BinSemGive_Impl returned %d\n", (int)return_code);
            /* Snapshot token bytes after call */
            for (i = 0; i < n; ++i)
            {
                after_bytes[i] = ((unsigned char *)&token)[i];
            }
            fprintf(f, "token after  (%u bytes): ", n);
            for (i = 0; i < n; ++i)
            {
                fprintf(f, "%02x", after_bytes[i]);
            }
            fprintf(f, "\n");

            /* Detect whether any byte changed */
            for (i = 0; i < n; ++i)
            {
                if (before_bytes[i] != after_bytes[i])
                {
                    fprintf(f, "token modified: first differing byte index %u (before=%02x, after=%02x)\n",
                            i, before_bytes[i], after_bytes[i]);
                    break;
                }
            }
            if (i == n)
            {
                fprintf(f, "token not modified by OS_BinSemGive_Impl\n");
            }
            fflush(f);
        }
    }

    if (f != NULL)
    {
        fprintf(f, "OS_BinSemGive EXIT: return_code=%d\n", (int)return_code);
        fclose(f);
    }

    return return_code;
}
