int32 OS_BinSemTimedWait(osal_id_t sem_id, uint32 msecs)
{
    OS_object_token_t token;
    int32             return_code;
    FILE *log = fopen("/tmp/logs/2715.txt", "a");

    /* Minimal entry probe */
    if (log != NULL)
    {
        fprintf(log, "OS_BinSemTimedWait ENTER: sem_id=%lu, msecs=%lu\n", (unsigned long)sem_id, (unsigned long)msecs);
        fflush(log);
    }

    /* Check Parameters */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_NONE, LOCAL_OBJID_TYPE, sem_id, &token);

    if (log != NULL)
    {
        fprintf(log, "OS_ObjectIdGetById returned %d\n", (int)return_code);

        /* Dump the raw contents of token to help determine if the implementation modifies it or only reads it. */
        {
            unsigned char *bytes = (unsigned char *)&token;
            size_t i;
            fprintf(log, "token raw (%zu bytes): ", sizeof(token));
            for (i = 0; i < sizeof(token); ++i)
            {
                fprintf(log, "%02X", (unsigned)bytes[i]);
            }
            fprintf(log, "\n");
        }
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        if (log != NULL)
        {
            fprintf(log, "Calling OS_BinSemTimedWait_Impl(&token, %lu)\n", (unsigned long)msecs);
            fflush(log);
        }

        return_code = OS_BinSemTimedWait_Impl(&token, msecs);

        if (log != NULL)
        {
            fprintf(log, "OS_BinSemTimedWait_Impl returned %d\n", (int)return_code);
            fflush(log);
        }
    }

    if (log != NULL)
    {
        fprintf(log, "OS_BinSemTimedWait EXIT: return_code=%d\n\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
