void OS_CleanUpObject(osal_id_t object_id, void *arg)
{
    uint32 *ObjectCount;
    FILE *logf = fopen("/tmp/logs/1360.txt", "a");
    if (logf == NULL)
    {
        /* Fallback if directory doesn't exist */
        logf = fopen("/tmp/1360.txt", "a");
    }

    if (logf)
    {
        fprintf(logf, "ENTRY: OS_CleanUpObject called; object_id=%d, arg=%p\n", (int)object_id, arg);
        fflush(logf);
    }

    ObjectCount = (uint32 *)arg;
    ++(*ObjectCount);

    if (logf)
    {
        fprintf(logf, "STATE: incremented ObjectCount -> %u\n", (unsigned)(*ObjectCount));
        fflush(logf);
    }

    int obj_type = OS_IdentifyObject(object_id);

    if (logf)
    {
        fprintf(logf, "STATE: OS_IdentifyObject returned type=%d\n", obj_type);
        fflush(logf);
    }

    switch (obj_type)
    {
        case OS_OBJECT_TYPE_OS_TASK:
            if (logf)
            {
                fprintf(logf, "ACTION: calling OS_TaskDelete for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            OS_TaskDelete(object_id);
            if (logf)
            {
                fprintf(logf, "RETURN: OS_TaskDelete returned for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            break;
        case OS_OBJECT_TYPE_OS_QUEUE:
            if (logf)
            {
                fprintf(logf, "ACTION: calling OS_QueueDelete for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            OS_QueueDelete(object_id);
            if (logf)
            {
                fprintf(logf, "RETURN: OS_QueueDelete returned for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            break;
        case OS_OBJECT_TYPE_OS_BINSEM:
            if (logf)
            {
                fprintf(logf, "ACTION: calling OS_BinSemDelete for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            OS_BinSemDelete(object_id);
            if (logf)
            {
                fprintf(logf, "RETURN: OS_BinSemDelete returned for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            break;
        case OS_OBJECT_TYPE_OS_COUNTSEM:
            if (logf)
            {
                fprintf(logf, "ACTION: calling OS_CountSemDelete for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            OS_CountSemDelete(object_id);
            if (logf)
            {
                fprintf(logf, "RETURN: OS_CountSemDelete returned for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            break;
        case OS_OBJECT_TYPE_OS_MUTEX:
            if (logf)
            {
                fprintf(logf, "ACTION: calling OS_MutSemDelete for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            OS_MutSemDelete(object_id);
            if (logf)
            {
                fprintf(logf, "RETURN: OS_MutSemDelete returned for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            break;
        case OS_OBJECT_TYPE_OS_MODULE:
            if (logf)
            {
                fprintf(logf, "ACTION: calling OS_ModuleUnload for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            OS_ModuleUnload(object_id);
            if (logf)
            {
                fprintf(logf, "RETURN: OS_ModuleUnload returned for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            break;
        case OS_OBJECT_TYPE_OS_TIMEBASE:
            if (logf)
            {
                fprintf(logf, "ACTION: calling OS_TimeBaseDelete for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            OS_TimeBaseDelete(object_id);
            if (logf)
            {
                fprintf(logf, "RETURN: OS_TimeBaseDelete returned for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            break;
        case OS_OBJECT_TYPE_OS_TIMECB:
            if (logf)
            {
                fprintf(logf, "ACTION: calling OS_TimerDelete for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            OS_TimerDelete(object_id);
            if (logf)
            {
                fprintf(logf, "RETURN: OS_TimerDelete returned for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            break;
        case OS_OBJECT_TYPE_OS_STREAM:
            if (logf)
            {
                fprintf(logf, "ACTION: calling OS_close for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            OS_close(object_id);
            if (logf)
            {
                fprintf(logf, "RETURN: OS_close returned for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            break;
        case OS_OBJECT_TYPE_OS_DIR:
            if (logf)
            {
                fprintf(logf, "ACTION: calling OS_DirectoryClose for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            OS_DirectoryClose(object_id);
            if (logf)
            {
                fprintf(logf, "RETURN: OS_DirectoryClose returned for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            break;
        case OS_OBJECT_TYPE_OS_CONDVAR:
            if (logf)
            {
                fprintf(logf, "ACTION: calling OS_CondVarDelete for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            OS_CondVarDelete(object_id);
            if (logf)
            {
                fprintf(logf, "RETURN: OS_CondVarDelete returned for object_id=%d\n", (int)object_id);
                fflush(logf);
            }
            break;
        default:
            if (logf)
            {
                fprintf(logf, "ACTION: no cleanup action for object_id=%d with type=%d\n", (int)object_id, obj_type);
                fflush(logf);
            }
            break;
    }

    if (logf)
    {
        fprintf(logf, "EXIT: OS_CleanUpObject completed for object_id=%d\n\n", (int)object_id);
        fclose(logf);
    }
}
