void OS_CleanUpObject(osal_id_t object_id, void *arg)
{
    uint32 *ObjectCount;
    FILE *fp = fopen("/tmp/logs/1363.txt", "a");

    if (fp)
    {
        fprintf(fp, "ENTER OS_CleanUpObject: object_id=%lu, arg=%p\n", (unsigned long)object_id, arg);
        fflush(fp);
    }

    ObjectCount = (uint32 *)arg;
    ++(*ObjectCount);

    if (fp)
    {
        /* Log pointer and updated count value (may crash here if arg == NULL as in original behavior) */
        fprintf(fp, "After increment: ObjectCount_ptr=%p, ObjectCount_value=%lu\n",
                (void *)ObjectCount, (unsigned long)(*ObjectCount));
        fflush(fp);
    }

    int objtype = OS_IdentifyObject(object_id);

    if (fp)
    {
        fprintf(fp, "OS_IdentifyObject returned: %d\n", objtype);
        fflush(fp);
    }

    switch (objtype)
    {
        case OS_OBJECT_TYPE_OS_TASK:
            if (fp) { fprintf(fp, "Calling OS_TaskDelete(%lu)\n", (unsigned long)object_id); fflush(fp); }
            OS_TaskDelete(object_id);
            if (fp) { fprintf(fp, "Returned from OS_TaskDelete(%lu)\n", (unsigned long)object_id); fflush(fp); }
            break;
        case OS_OBJECT_TYPE_OS_QUEUE:
            if (fp) { fprintf(fp, "Calling OS_QueueDelete(%lu)\n", (unsigned long)object_id); fflush(fp); }
            OS_QueueDelete(object_id);
            if (fp) { fprintf(fp, "Returned from OS_QueueDelete(%lu)\n", (unsigned long)object_id); fflush(fp); }
            break;
        case OS_OBJECT_TYPE_OS_BINSEM:
            if (fp) { fprintf(fp, "Calling OS_BinSemDelete(%lu)\n", (unsigned long)object_id); fflush(fp); }
            OS_BinSemDelete(object_id);
            if (fp) { fprintf(fp, "Returned from OS_BinSemDelete(%lu)\n", (unsigned long)object_id); fflush(fp); }
            break;
        case OS_OBJECT_TYPE_OS_COUNTSEM:
            if (fp) { fprintf(fp, "Calling OS_CountSemDelete(%lu)\n", (unsigned long)object_id); fflush(fp); }
            OS_CountSemDelete(object_id);
            if (fp) { fprintf(fp, "Returned from OS_CountSemDelete(%lu)\n", (unsigned long)object_id); fflush(fp); }
            break;
        case OS_OBJECT_TYPE_OS_MUTEX:
            if (fp) { fprintf(fp, "Calling OS_MutSemDelete(%lu)\n", (unsigned long)object_id); fflush(fp); }
            OS_MutSemDelete(object_id);
            if (fp) { fprintf(fp, "Returned from OS_MutSemDelete(%lu)\n", (unsigned long)object_id); fflush(fp); }
            break;
        case OS_OBJECT_TYPE_OS_MODULE:
            if (fp) { fprintf(fp, "Calling OS_ModuleUnload(%lu)\n", (unsigned long)object_id); fflush(fp); }
            OS_ModuleUnload(object_id);
            if (fp) { fprintf(fp, "Returned from OS_ModuleUnload(%lu)\n", (unsigned long)object_id); fflush(fp); }
            break;
        case OS_OBJECT_TYPE_OS_TIMEBASE:
            if (fp) { fprintf(fp, "Calling OS_TimeBaseDelete(%lu)\n", (unsigned long)object_id); fflush(fp); }
            OS_TimeBaseDelete(object_id);
            if (fp) { fprintf(fp, "Returned from OS_TimeBaseDelete(%lu)\n", (unsigned long)object_id); fflush(fp); }
            break;
        case OS_OBJECT_TYPE_OS_TIMECB:
            if (fp) { fprintf(fp, "Calling OS_TimerDelete(%lu)\n", (unsigned long)object_id); fflush(fp); }
            OS_TimerDelete(object_id);
            if (fp) { fprintf(fp, "Returned from OS_TimerDelete(%lu)\n", (unsigned long)object_id); fflush(fp); }
            break;
        case OS_OBJECT_TYPE_OS_STREAM:
            if (fp) { fprintf(fp, "Calling OS_close(%lu)\n", (unsigned long)object_id); fflush(fp); }
            OS_close(object_id);
            if (fp) { fprintf(fp, "Returned from OS_close(%lu)\n", (unsigned long)object_id); fflush(fp); }
            break;
        case OS_OBJECT_TYPE_OS_DIR:
            if (fp) { fprintf(fp, "Calling OS_DirectoryClose(%lu)\n", (unsigned long)object_id); fflush(fp); }
            OS_DirectoryClose(object_id);
            if (fp) { fprintf(fp, "Returned from OS_DirectoryClose(%lu)\n", (unsigned long)object_id); fflush(fp); }
            break;
        case OS_OBJECT_TYPE_OS_CONDVAR:
            if (fp) { fprintf(fp, "Calling OS_CondVarDelete(%lu)\n", (unsigned long)object_id); fflush(fp); }
            OS_CondVarDelete(object_id);
            if (fp) { fprintf(fp, "Returned from OS_CondVarDelete(%lu)\n", (unsigned long)object_id); fflush(fp); }
            break;
        default:
            if (fp) { fprintf(fp, "Default case reached for object_id=%lu\n", (unsigned long)object_id); fflush(fp); }
            break;
    }

    if (fp)
    {
        fprintf(fp, "EXIT OS_CleanUpObject: object_id=%lu\n\n", (unsigned long)object_id);
        fclose(fp);
    }
}
