int32 OS_CloseAllFiles(void)
{
    /* Include standard headers locally to ensure logging functions are available
     * This keeps the function self-contained for testing purposes. */
    #include <stdio.h>
    #include <errno.h>
    #include <string.h>

    int32            return_code;
    int32            close_code;
    OS_object_iter_t iter;

    /* Open log file for append. Tests will inspect /tmp/logs/3333.txt */
    const char *log_path = "/tmp/logs/3333.txt";
    FILE *log = fopen(log_path, "a");
    if (log == NULL)
    {
        /* If logging can't be opened, try to at least attempt to continue
         * without crashing the original logic. */
        /* Note: We do not return here because the original function must run. */
    }

    if (log)
    {
        fprintf(log, "=== OS_CloseAllFiles entered ===\n");
        fprintf(log, "Initial local iterator sizeof = %zu, iterator address = %p\n", sizeof(iter), (void *)&iter);
        fflush(log);
    }

    return_code = OS_SUCCESS;

    if (log)
    {
        fprintf(log, "Calling OS_ObjectIdIterateActive(LOCAL_OBJID_TYPE, &iter)\n");
        fflush(log);
    }

    OS_ObjectIdIterateActive(LOCAL_OBJID_TYPE, &iter);

    /* Iterate over active objects and attempt to close file entries */
    int loop_count = 0;
    while (OS_ObjectIdIteratorGetNext(&iter))
    {
        if (log)
        {
            fprintf(log, "Iterator GetNext returned entry #%d, iterator addr=%p\n", loop_count, (void *)&iter);
            fflush(log);
        }

        /* call OS_close() on the entry referred to by the iterator */
        close_code = OS_ObjectIdIteratorProcessEntry(&iter, OS_FileIteratorClose);

        if (log)
        {
            fprintf(log, "Processed entry #%d -> close_code = %d\n", loop_count, (int)close_code);
            /* Capture the running return_code as it evolves to help correlate which
             * entry produced a non-success result. */
            fprintf(log, "Before update: return_code = %d\n", (int)return_code);
            fflush(log);
        }

        if (close_code != OS_SUCCESS)
        {
            if (log)
            {
                fprintf(log, "Non-success close_code detected for entry #%d: %d\n", loop_count, (int)close_code);
                fflush(log);
            }

            /* Preserve original behavior: collapse non-success into return_code */
            return_code = close_code;

            if (log)
            {
                fprintf(log, "Updated return_code to %d due to entry #%d\n", (int)return_code, loop_count);
                fflush(log);
            }
        }

        loop_count++;
    }

    if (log)
    {
        fprintf(log, "Iterator loop finished, destroying iterator. Total processed = %d\n", loop_count);
        fflush(log);
    }

    OS_ObjectIdIteratorDestroy(&iter);

    if (log)
    {
        fprintf(log, "OS_ObjectIdIteratorDestroy returned. Final return_code = %d\n", (int)return_code);
        fprintf(log, "=== OS_CloseAllFiles exiting ===\n\n");
        fclose(log);
    }

    return return_code;
}
