int32 OS_CloseFileByName(const char *Filename)
{
    int32                        return_code;
    int32                        close_code;
    OS_object_iter_t             iter;
    OS_stream_internal_record_t *stream;
    FILE *log = fopen("/tmp/logs/623.txt", "a");

    /* Minimal entry log so that the function invocation is always recorded */
    if (log)
    {
        fprintf(log, "ENTER: OS_CloseFileByName Filename=%p\n", (const void *)Filename);
        fflush(log);
    }

    /* Check parameters */
    OS_CHECK_POINTER(Filename);

    return_code = OS_FS_ERR_PATH_INVALID;

    OS_ObjectIdIterateActive(LOCAL_OBJID_TYPE, &iter);

    while (OS_ObjectIdIteratorGetNext(&iter))
    {
        stream = OS_OBJECT_TABLE_GET(OS_stream_table, iter.token);

        if (log)
        {
            fprintf(log, "ITER: token=%lu stream=%p stream_name_ptr=%p socket_domain=%ld\n",
                    (unsigned long)iter.token,
                    (void *)stream,
                    (void *)((stream) ? stream->stream_name : NULL),
                    (long)((stream) ? stream->socket_domain : -1));
            fflush(log);
        }

        /* Preserve original short-circuit behavior: only perform strcmp when socket_domain is INVALID */
        if (stream->socket_domain == OS_SocketDomain_INVALID)
        {
            if (log)
            {
                fprintf(log, "ABOUT_TO_COMPARE: stream->stream_name=%p Filename=%p\n",
                        (void *)stream->stream_name, (const void *)Filename);
                fflush(log);
            }

            /* The following strcmp is the original operation that can read until a terminating '\0' is found.
             * If either operand lacks a terminating NUL within its accessible storage this call may read out-of-bounds
             * (this is the condition being investigated). We still call strcmp to preserve original behavior. */
            int cmp_result = strcmp(stream->stream_name, Filename);

            if (log)
            {
                fprintf(log, "COMPARE_RESULT: strcmp(...) = %d (0 means match)\n", cmp_result);
                fflush(log);
            }

            if (cmp_result == 0)
            {
                /* call OS_close() on the entry referred to by the iterator */
                close_code = OS_ObjectIdIteratorProcessEntry(&iter, OS_FileIteratorClose);

                if (log)
                {
                    fprintf(log, "PROCESS_ENTRY: close_code=%ld\n", (long)close_code);
                    fflush(log);
                }

                if (return_code == OS_FS_ERR_PATH_INVALID || close_code != OS_SUCCESS)
                {
                    return_code = close_code;

                    if (log)
                    {
                        fprintf(log, "RETURN_UPDATED: return_code=%ld\n", (long)return_code);
                        fflush(log);
                    }
                }
            }
        }
        else
        {
            if (log)
            {
                fprintf(log, "SKIP: socket_domain != OS_SocketDomain_INVALID (value=%ld)\n", (long)stream->socket_domain);
                fflush(log);
            }
        }
    }

    OS_ObjectIdIteratorDestroy(&iter);

    if (log)
    {
        fprintf(log, "EXIT: OS_CloseFileByName returning %ld\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
