int32 OS_CloseFileByName(const char *Filename)
{
    int32                        return_code;
    int32                        close_code;
    OS_object_iter_t             iter;
    OS_stream_internal_record_t *stream;

    /* Open a log file to record control flow and state for diagnostics */
    FILE *log = fopen("/tmp/logs/623.txt", "a");
    if (log)
    {
        fprintf(log, "OS_CloseFileByName ENTER: Filename ptr=%p\n", (const void *)Filename);
        fflush(log);
    }

    /* Check parameters */
    OS_CHECK_POINTER(Filename);

    return_code = OS_FS_ERR_PATH_INVALID;

    OS_ObjectIdIterateActive(LOCAL_OBJID_TYPE, &iter);

    while (OS_ObjectIdIteratorGetNext(&iter))
    {
        stream = OS_OBJECT_TABLE_GET(OS_stream_table, iter.token);

        if (log)
        {
            /* Log iterator token and stream pointer and socket domain */
            fprintf(log, "Iter token=%u, stream_ptr=%p, socket_domain=%d\n",
                    (unsigned int)iter.token, (void *)stream, (int)stream->socket_domain);

            /* Inspect stream->stream_name safely: its object size is known (array) */
            size_t sn_size = sizeof(stream->stream_name);
            void *nulpos = memchr(stream->stream_name, '\0', sn_size);

            if (nulpos != NULL)
            {
                size_t slen = (const char *)nulpos - (const char *)stream->stream_name;
                /* Print the NUL-terminated content up to slen */
                fprintf(log, "stream_name: NUL within buffer, length=%zu, content=\"%.*s\"\n",
                        slen, (int)slen, stream->stream_name);
            }
            else
            {
                /* No NUL found within buffer bounds */
                fprintf(log, "stream_name: NO NUL within buffer of size=%zu; hex dump up to 16 bytes:\n", sn_size);
                size_t dump = sn_size < 16 ? sn_size : 16;
                for (size_t i = 0; i < dump; ++i)
                {
                    fprintf(log, "%02X ", (unsigned char)stream->stream_name[i]);
                }
                fprintf(log, "\n");
            }

            /* For safety, do not attempt to probe Filename contents beyond observing the pointer
             * because Filename may point to an object whose bounds are unknown here. Log only pointer. */
            fprintf(log, "About to compare stream_name with Filename ptr=%p using strcmp\n", (const void *)Filename);
            fflush(log);
        }

        if (stream->socket_domain == OS_SocketDomain_INVALID && (strcmp(stream->stream_name, Filename) == 0))
        {
            if (log)
            {
                fprintf(log, "strcmp matched for token=%u\n", (unsigned int)iter.token);
                fflush(log);
            }

            /* call OS_close() on the entry referred to by the iterator */
            close_code = OS_ObjectIdIteratorProcessEntry(&iter, OS_FileIteratorClose);

            if (log)
            {
                fprintf(log, "Processed entry: token=%u, close_code=%d\n", (unsigned int)iter.token, (int)close_code);
                fflush(log);
            }

            if (return_code == OS_FS_ERR_PATH_INVALID || close_code != OS_SUCCESS)
            {
                if (log)
                {
                    fprintf(log, "Updating return_code: was %d, now %d\n", (int)return_code, (int)close_code);
                    fflush(log);
                }
                return_code = close_code;
            }
        }
        else
        {
            if (log)
            {
                fprintf(log, "No match for token=%u (socket_domain=%d)\n", (unsigned int)iter.token, (int)stream->socket_domain);
                fflush(log);
            }
        }
    }

    OS_ObjectIdIteratorDestroy(&iter);

    if (log)
    {
        fprintf(log, "OS_CloseFileByName EXIT: return_code=%d\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
