int32 OS_CloseFileByName(const char *Filename)
{
    int32                        return_code;
    int32                        close_code;
    OS_object_iter_t             iter;
    OS_stream_internal_record_t *stream;
    FILE *log = fopen("/tmp/logs/625.txt", "a");

    if (log != NULL)
    {
        fprintf(log, "OS_CloseFileByName ENTER: Filename='%s'\n", Filename ? Filename : "(null)");
    }

    /* Check parameters */
    OS_CHECK_POINTER(Filename);

    return_code = OS_FS_ERR_PATH_INVALID;

    OS_ObjectIdIterateActive(LOCAL_OBJID_TYPE, &iter);

    while (OS_ObjectIdIteratorGetNext(&iter))
    {
        stream = OS_OBJECT_TABLE_GET(OS_stream_table, iter.token);

        /* Log the iterator token and the stream pointer and fields that will be inspected. */
        if (log != NULL)
        {
            /* Note: accessing stream->... here mirrors the original code's dereference behavior so
               logs reflect the same control-flow/state as observed by the original implementation. */
            fprintf(log, "Iterator token=%u, stream_addr=%p\n", (unsigned)iter.token, (void *)stream);
            /* Accessing members as in original code so logs show the same values used by the logic */
            if (stream != NULL)
            {
                fprintf(log, " stream->socket_domain=%d, stream->stream_name='%s'\n",
                        (int)stream->socket_domain, stream->stream_name ? stream->stream_name : "(null)");
            }
            else
            {
                fprintf(log, " stream is NULL (will dereference as in original code)\n");
            }
        }

        if (stream->socket_domain == OS_SocketDomain_INVALID && (strcmp(stream->stream_name, Filename) == 0))
        {
            if (log != NULL)
            {
                fprintf(log, " MATCH: token=%u name='%s'\n", (unsigned)iter.token, Filename);
            }

            /* call OS_close() on the entry referred to by the iterator */
            close_code = OS_ObjectIdIteratorProcessEntry(&iter, OS_FileIteratorClose);

            if (log != NULL)
            {
                fprintf(log, " ProcessEntry returned close_code=%d\n", close_code);
            }

            if (return_code == OS_FS_ERR_PATH_INVALID || close_code != OS_SUCCESS)
            {
                if (log != NULL)
                {
                    fprintf(log, " Updating return_code from %d to %d\n", return_code, close_code);
                }
                return_code = close_code;
            }
        }
        else
        {
            if (log != NULL)
            {
                fprintf(log, " NO_MATCH for token=%u\n", (unsigned)iter.token);
            }
        }
    }

    OS_ObjectIdIteratorDestroy(&iter);

    if (log != NULL)
    {
        fprintf(log, "OS_CloseFileByName EXIT return_code=%d\n", return_code);
        fclose(log);
    }

    return return_code;
}
