int32 OS_CloseFileByName(const char *Filename)
{
    int32                        return_code;
    int32                        close_code;
    OS_object_iter_t             iter;
    OS_stream_internal_record_t *stream;

    /* Check parameters */
    OS_CHECK_POINTER(Filename);

    /* Open log file for append. If this fails, proceed without logging. */
    FILE *logfp = fopen("/tmp/logs/625.txt", "a");
    if (logfp)
    {
        fprintf(logfp, "ENTER OS_CloseFileByName: Filename=%p\n", (const void *)Filename);
        if (Filename != NULL)
        {
            /* limit printed filename length to avoid excessive output */
            fprintf(logfp, "  Filename contents=\"%s\"\n", Filename);
        }
        fflush(logfp);
    }

    return_code = OS_FS_ERR_PATH_INVALID;

    OS_ObjectIdIterateActive(LOCAL_OBJID_TYPE, &iter);

    while (OS_ObjectIdIteratorGetNext(&iter))
    {
        stream = OS_OBJECT_TABLE_GET(OS_stream_table, iter.token);

        if (logfp)
        {
            fprintf(logfp, "  ITER token=%lu, stream_ptr=%p\n", (unsigned long)iter.token, (void *)stream);
            if (stream != NULL)
            {
                fprintf(logfp, "    socket_domain=%d, stream_name=%p\n", (int)stream->socket_domain, (void *)stream->stream_name);
                if (stream->stream_name != NULL)
                {
                    fprintf(logfp, "    stream_name contents=\"%s\"\n", stream->stream_name);
                }
            }
            fflush(logfp);
        }

        /* Original matching logic: only reads the stream record */
        if (stream != NULL && stream->socket_domain == OS_SocketDomain_INVALID && (strcmp(stream->stream_name, Filename) == 0))
        {
            if (logfp)
            {
                fprintf(logfp, "    MATCH found for token=%lu (name=\"%s\")\n", (unsigned long)iter.token, stream->stream_name);
                fflush(logfp);
            }

            /* call OS_close() on the entry referred to by the iterator */
            close_code = OS_ObjectIdIteratorProcessEntry(&iter, OS_FileIteratorClose);

            if (logfp)
            {
                fprintf(logfp, "    OS_ObjectIdIteratorProcessEntry returned %ld\n", (long)close_code);
                fflush(logfp);
            }

            if (return_code == OS_FS_ERR_PATH_INVALID || close_code != OS_SUCCESS)
            {
                if (logfp)
                {
                    fprintf(logfp, "    Updating return_code: was %ld, now %ld\n", (long)return_code, (long)close_code);
                    fflush(logfp);
                }

                return_code = close_code;
            }
        }
        else
        {
            if (logfp)
            {
                fprintf(logfp, "    No match for token=%lu\n", (unsigned long)iter.token);
                fflush(logfp);
            }
        }
    }

    OS_ObjectIdIteratorDestroy(&iter);

    if (logfp)
    {
        fprintf(logfp, "EXIT OS_CloseFileByName: return_code=%ld\n", (long)return_code);
        fclose(logfp);
    }

    return return_code;
}
