int32 OS_CondVarBroadcast(osal_id_t var_id)
{
    OS_object_token_t token;
    int32             return_code;

    /* Logging probe: open log file (append) to record control flow and state */
    FILE *logf = fopen("/tmp/logs/3203.txt", "a");
    if (logf != NULL)
    {
        /* Log entry and the raw var_id value (cast to unsigned long long for portability) */
        fprintf(logf, "OS_CondVarBroadcast ENTRY: var_id=%llu\n", (unsigned long long) var_id);
        fprintf(logf, "Calling OS_ObjectIdGetById(OS_LOCK_MODE_NONE, OS_OBJECT_TYPE_OS_CONDVAR, var_id, &token)\n");
        fflush(logf);
    }

    /* Check Parameters (preserve original logic) */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_NONE, OS_OBJECT_TYPE_OS_CONDVAR, var_id, &token);

    if (logf != NULL)
    {
        fprintf(logf, "OS_ObjectIdGetById returned %d\n", (int)return_code);
        fflush(logf);
    }

    if (return_code == OS_SUCCESS)
    {
        /* Dump the raw contents of the token to help reproduce any downstream misuse
         * (This prints the address and a hex-bytes dump of the token object.)
         */
        if (logf != NULL)
        {
            unsigned char *bytes = (unsigned char *)&token;
            size_t len = sizeof(token);
            fprintf(logf, "Token address=%p size=%zu bytes\n", (void *)&token, len);
            fprintf(logf, "Token bytes:");
            for (size_t i = 0; i < len; ++i)
            {
                fprintf(logf, " %02x", bytes[i]);
            }
            fprintf(logf, "\n");
            fflush(logf);
        }

        /* Call implementation (preserve original behavior) */
        return_code = OS_CondVarBroadcast_Impl(&token);

        if (logf != NULL)
        {
            fprintf(logf, "OS_CondVarBroadcast_Impl returned %d\n", (int)return_code);
            fflush(logf);
        }
    }

    if (logf != NULL)
    {
        fprintf(logf, "OS_CondVarBroadcast EXIT: return_code=%d\n\n", (int)return_code);
        fclose(logf);
    }

    return return_code;
}
