int32 OS_CondVarCreate(osal_id_t *var_id, const char *var_name, uint32 options)
{
    int32                         return_code;
    OS_object_token_t             token;
    OS_condvar_internal_record_t *condvar;
    FILE *log = fopen("/tmp/logs/760.txt", "a");

    if (log)
    {
        fprintf(log, "ENTER OS_CondVarCreate: var_name=%s options=%u var_id_ptr=%p\n",
                var_name ? var_name : "(null)", (unsigned)options, (void *)var_id);
        fflush(log);
    }

    /* Check parameters */
    OS_CHECK_POINTER(var_id);
    OS_CHECK_APINAME(var_name);

    /* Note - the common ObjectIdAllocate routine will lock the object type and leave it locked. */
    return_code = OS_ObjectIdAllocateNew(OS_OBJECT_TYPE_OS_CONDVAR, var_name, &token);

    if (log)
    {
        fprintf(log, "OS_ObjectIdAllocateNew returned %d\n", (int)return_code);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        condvar = OS_OBJECT_TABLE_GET(OS_condvar_table, token);

        if (log)
        {
            fprintf(log, "Allocated token (local token addr=%p). condvar ptr=%p\n", (void *)&token, (void *)condvar);
            fflush(log);
        }

        /* Reset the table entry and save the name */
        OS_OBJECT_INIT(token, condvar, obj_name, var_name);

        /* Now call the OS-specific implementation.  This reads info from the table. */
        if (log)
        {
            fprintf(log, "Calling OS_CondVarCreate_Impl with token addr=%p options=%u\n", (void *)&token, (unsigned)options);
            fflush(log);
        }
        return_code = OS_CondVarCreate_Impl(&token, options);

        if (log)
        {
            fprintf(log, "OS_CondVarCreate_Impl returned %d\n", (int)return_code);
            fflush(log);
        }

        /* Check result, finalize record, and unlock global table. */
        return_code = OS_ObjectIdFinalizeNew(return_code, &token, var_id);

        if (log)
        {
            if (return_code == OS_SUCCESS && var_id != NULL)
            {
                /* Print the assigned id if success */
                fprintf(log, "OS_ObjectIdFinalizeNew succeeded, var_id=%llu\n", (unsigned long long)*var_id);
            }
            else
            {
                fprintf(log, "OS_ObjectIdFinalizeNew returned %d\n", (int)return_code);
            }
            fflush(log);
            fclose(log);
            log = NULL;
        }
    }
    else
    {
        if (log)
        {
            fprintf(log, "OS_ObjectIdAllocateNew failed with %d; returning\n", (int)return_code);
            fflush(log);
            fclose(log);
            log = NULL;
        }
    }

    return return_code;
}
