int32 OS_CondVarCreate(osal_id_t *var_id, const char *var_name, uint32 options)
{
    int32                         return_code;
    OS_object_token_t             token;
    OS_condvar_internal_record_t *condvar;
    FILE *log_fp = NULL;

    /* Try to open the log file. If this fails, continue without logging. */
    log_fp = fopen("/tmp/logs/763.txt", "a");
    if (log_fp)
    {
        fprintf(log_fp, "OS_CondVarCreate ENTER: var_id=%p var_name=%p options=%u\n",
                (void *)var_id, (const void *)var_name, (unsigned)options);
        fflush(log_fp);
    }

    /* Check parameters */
    if (log_fp)
    {
        fprintf(log_fp, "OS_CondVarCreate: Calling OS_CHECK_POINTER(var_id)\n");
        fflush(log_fp);
    }
    OS_CHECK_POINTER(var_id);

    if (log_fp)
    {
        fprintf(log_fp, "OS_CondVarCreate: Calling OS_CHECK_APINAME(var_name) (note: contents not inspected to avoid side-effects)\n");
        fflush(log_fp);
    }
    OS_CHECK_APINAME(var_name);

    /* Note - the common ObjectIdAllocate routine will lock the object type and leave it locked. */
    if (log_fp)
    {
        fprintf(log_fp, "OS_CondVarCreate: Calling OS_ObjectIdAllocateNew with var_name=%p\n", (const void *)var_name);
        fflush(log_fp);
    }
    return_code = OS_ObjectIdAllocateNew(OS_OBJECT_TYPE_OS_CONDVAR, var_name, &token);

    if (log_fp)
    {
        fprintf(log_fp, "OS_CondVarCreate: OS_ObjectIdAllocateNew returned %d\n", (int)return_code);
        fflush(log_fp);
    }

    if (return_code == OS_SUCCESS)
    {
        condvar = OS_OBJECT_TABLE_GET(OS_condvar_table, token);

        if (log_fp)
        {
            fprintf(log_fp, "OS_CondVarCreate: Allocation succeeded, condvar table entry at %p\n", (void *)condvar);
            fflush(log_fp);
        }

        /* Reset the table entry and save the name */
        if (log_fp)
        {
            fprintf(log_fp, "OS_CondVarCreate: Calling OS_OBJECT_INIT(token, condvar, obj_name, var_name)\n");
            fflush(log_fp);
        }
        OS_OBJECT_INIT(token, condvar, obj_name, var_name);

        /* Now call the OS-specific implementation.  This reads info from the table. */
        if (log_fp)
        {
            fprintf(log_fp, "OS_CondVarCreate: Calling OS_CondVarCreate_Impl(&token, options)\n");
            fflush(log_fp);
        }
        return_code = OS_CondVarCreate_Impl(&token, options);

        if (log_fp)
        {
            fprintf(log_fp, "OS_CondVarCreate: OS_CondVarCreate_Impl returned %d\n", (int)return_code);
            fflush(log_fp);
        }

        /* Check result, finalize record, and unlock global table. */
        if (log_fp)
        {
            fprintf(log_fp, "OS_CondVarCreate: Calling OS_ObjectIdFinalizeNew(return_code=%d, &token, var_id)\n", (int)return_code);
            fflush(log_fp);
        }
        return_code = OS_ObjectIdFinalizeNew(return_code, &token, var_id);

        if (log_fp)
        {
            fprintf(log_fp, "OS_CondVarCreate: OS_ObjectIdFinalizeNew returned %d\n", (int)return_code);
            fflush(log_fp);
        }
    }

    if (log_fp)
    {
        fprintf(log_fp, "OS_CondVarCreate EXIT: returning %d\n", (int)return_code);
        fclose(log_fp);
    }

    return return_code;
}
