int32 OS_CondVarGetInfo(osal_id_t var_id, OS_condvar_prop_t *condvar_prop)
{
    OS_common_record_t *record;
    int32               return_code;
    OS_object_token_t   token;

    /* Logging: open a file and record entry and parameters. This uses stdio functions
     * which are expected to be available in the build environment. The logs are
     * appended so multiple runs can be inspected. */
    {
        FILE *logf = fopen("/tmp/logs/1510.txt", "a");
        if (logf != NULL)
        {
            /* Log function entry and parameter values */
            fprintf(logf, "ENTER: OS_CondVarGetInfo var_id=%llu condvar_prop=%p\n",
                    (unsigned long long)var_id, (void *)condvar_prop);
            fclose(logf);
        }
    }

    /* Check parameters */
    OS_CHECK_POINTER(condvar_prop);

    memset(condvar_prop, 0, sizeof(OS_condvar_prop_t));

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_OBJECT_TYPE_OS_CONDVAR, var_id, &token);

    /* Log the result of ObjectIdGetById */
    {
        FILE *logf = fopen("/tmp/logs/1510.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "After OS_ObjectIdGetById: return_code=%d\n", (int)return_code);
            fclose(logf);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_condvar_table, token);

        /* Log details about the record found */
        {
            FILE *logf = fopen("/tmp/logs/1510.txt", "a");
            if (logf != NULL)
            {
                /* record->name_entry is expected to be a NUL-terminated string */
                fprintf(logf, "Record found: name_entry=%s creator=%u token=%p\n",
                        record->name_entry, (unsigned)record->creator, (void *)&token);
                fclose(logf);
            }
        }

        strncpy(condvar_prop->name, record->name_entry, sizeof(condvar_prop->name) - 1);
        condvar_prop->creator = record->creator;

        /* Log before calling the implementation function */
        {
            FILE *logf = fopen("/tmp/logs/1510.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "Calling OS_CondVarGetInfo_Impl with token=%p\n", (void *)&token);
                fclose(logf);
            }
        }

        return_code = OS_CondVarGetInfo_Impl(&token, condvar_prop);

        /* Log the result from the implementation */
        {
            FILE *logf = fopen("/tmp/logs/1510.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "OS_CondVarGetInfo_Impl returned %d\n", (int)return_code);
                fclose(logf);
            }
        }

        OS_ObjectIdRelease(&token);
    }

    /* Final log before returning */
    {
        FILE *logf = fopen("/tmp/logs/1510.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "EXIT: OS_CondVarGetInfo returning %d\n", (int)return_code);
            fclose(logf);
        }
    }

    return return_code;
}
