int32 OS_CondVarGetInfo(osal_id_t var_id, OS_condvar_prop_t *condvar_prop)
{
    OS_common_record_t *record;
    int32               return_code;
    OS_object_token_t   token;

    /* Minimal entry probe: record that the function was entered and parameters */
    {
        FILE *logf = fopen("/tmp/logs/1518.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "ENTER OS_CondVarGetInfo: var_id=%u, condvar_prop=%p\n", (unsigned) var_id, (void *) condvar_prop);
            fclose(logf);
        }
    }

    /* Check parameters (preserve original behaviour) */
    OS_CHECK_POINTER(condvar_prop);

    /* Clear output structure (same as original) */
    memset(condvar_prop, 0, sizeof(OS_condvar_prop_t));

    /* Save snapshot after memset (so we can compare after Impl call) */
    OS_condvar_prop_t pre_impl_snapshot;
    memset(&pre_impl_snapshot, 0xA5, sizeof(pre_impl_snapshot)); /* fill with recognisable pattern */

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_OBJECT_TYPE_OS_CONDVAR, var_id, &token);

    /* Log the result of ObjectIdGetById */
    {
        FILE *logf = fopen("/tmp/logs/1518.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "OS_ObjectIdGetById returned %d\n", (int) return_code);
            fclose(logf);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_condvar_table, token);

        strncpy(condvar_prop->name, record->name_entry, sizeof(condvar_prop->name) - 1);
        condvar_prop->creator = record->creator;

        /* take a snapshot of condvar_prop immediately before calling Impl */
        memcpy(&pre_impl_snapshot, condvar_prop, sizeof(OS_condvar_prop_t));

        /* Log state before Impl call */
        {
            FILE *logf = fopen("/tmp/logs/1518.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "Before OS_CondVarGetInfo_Impl: token=%p, name=\"%s\", creator=%u\n",
                        (void *)&token,
                        condvar_prop->name,
                        (unsigned) condvar_prop->creator);
                fclose(logf);
            }
        }

        /* Call platform-specific implementation (preserve original behaviour) */
        return_code = OS_CondVarGetInfo_Impl(&token, condvar_prop);

        /* Log return from Impl and snapshot comparison */
        {
            int changed = (memcmp(&pre_impl_snapshot, condvar_prop, sizeof(OS_condvar_prop_t)) != 0);
            FILE *logf = fopen("/tmp/logs/1518.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "OS_CondVarGetInfo_Impl returned %d\n", (int) return_code);
                fprintf(logf, "After OS_CondVarGetInfo_Impl: name=\"%s\", creator=%u\n",
                        condvar_prop->name,
                        (unsigned) condvar_prop->creator);
                if (changed)
                {
                    fprintf(logf, "NOTE: condvar_prop CHANGED by Impl (byte-wise comparison)\n");
                }
                else
                {
                    fprintf(logf, "NOTE: condvar_prop UNCHANGED by Impl (byte-wise comparison)\n");
                }
                fclose(logf);
            }
        }

        OS_ObjectIdRelease(&token);

        /* Log token release */
        {
            FILE *logf = fopen("/tmp/logs/1518.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "OS_ObjectIdRelease called for token=%p\n", (void *)&token);
                fclose(logf);
            }
        }
    }

    return return_code;
}
