int32 OS_CondVarGetInfo(osal_id_t var_id, OS_condvar_prop_t *condvar_prop)
{
    OS_common_record_t *record;
    int32               return_code;
    OS_object_token_t   token;
    FILE *log = NULL;
    char local_name[128];

    /* Minimal entry log so callers/tests can detect the function was entered */
    log = fopen("/tmp/logs/1515.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER: OS_CondVarGetInfo var_id=%ld condvar_prop=%p\n", (long)var_id, (void *)condvar_prop);
        fflush(log);
    }

    /* Check parameters */
    OS_CHECK_POINTER(condvar_prop);

    if (log)
    {
        fprintf(log, "After OS_CHECK_POINTER\n");
        fflush(log);
    }

    memset(condvar_prop, 0, sizeof(OS_condvar_prop_t));

    if (log)
    {
        fprintf(log, "Zeroed condvar_prop at %p\n", (void *)condvar_prop);
        fflush(log);
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_OBJECT_TYPE_OS_CONDVAR, var_id, &token);

    if (log)
    {
        fprintf(log, "OS_ObjectIdGetById returned %ld\n", (long)return_code);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_condvar_table, token);

        if (log)
        {
            fprintf(log, "record=%p\n", (void *)record);
            if (record)
            {
                /* Safely copy a prefix of the name_entry for logging. This mirrors the original read usage. */
                memset(local_name, 0, sizeof(local_name));
                strncpy(local_name, record->name_entry, sizeof(local_name) - 1);
                local_name[sizeof(local_name) - 1] = '\0';

                fprintf(log, "record->name_entry (prefix): '%s'\n", local_name);
                fprintf(log, "record->creator=%ld\n", (long)record->creator);
                fflush(log);
            }
        }

        /* Preserve original behavior: copy name and creator into the output structure */
        strncpy(condvar_prop->name, record->name_entry, sizeof(condvar_prop->name) - 1);
        condvar_prop->creator = record->creator;

        if (log)
        {
            fprintf(log, "Copied name and creator to condvar_prop (condvar_prop=%p)\n", (void *)condvar_prop);
            fflush(log);
        }

        return_code = OS_CondVarGetInfo_Impl(&token, condvar_prop);

        if (log)
        {
            fprintf(log, "OS_CondVarGetInfo_Impl returned %ld\n", (long)return_code);
            fflush(log);
        }

        OS_ObjectIdRelease(&token);

        if (log)
        {
            fprintf(log, "Released token\n");
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_CondVarGetInfo return_code=%ld\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
