int32 OS_CondVarLock(osal_id_t var_id)
{
    OS_object_token_t token;
    int32             return_code;

    /* Logging: open log file for append. Best-effort; do not change behavior if logging fails. */
    FILE *logf = fopen("/tmp/logs/1403.txt", "a");
    if (logf)
    {
        fprintf(logf, "ENTER OS_CondVarLock: var_id=%u\n", (unsigned) var_id);
        fflush(logf);
    }

    /* Check Parameters (preserve original logic) */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_NONE, OS_OBJECT_TYPE_OS_CONDVAR, var_id, &token);

    if (logf)
    {
        fprintf(logf, "OS_ObjectIdGetById returned %d for var_id=%u; token_addr=%p\n",
                (int)return_code, (unsigned)var_id, (void *)&token);
        fflush(logf);
    }

    if (return_code == OS_SUCCESS)
    {
        if (logf)
        {
            fprintf(logf, "Calling OS_CondVarLock_Impl for var_id=%u\n", (unsigned) var_id);
            fflush(logf);
        }

        return_code = OS_CondVarLock_Impl(&token);

        if (logf)
        {
            fprintf(logf, "OS_CondVarLock_Impl returned %d for var_id=%u\n", (int)return_code, (unsigned)var_id);
            fflush(logf);
        }
    }
    else
    {
        if (logf)
        {
            fprintf(logf, "Skipping OS_CondVarLock_Impl due to OS_ObjectIdGetById failure (%d) for var_id=%u\n",
                    (int)return_code, (unsigned)var_id);
            fflush(logf);
        }
    }

    if (logf)
    {
        fprintf(logf, "EXIT OS_CondVarLock: return_code=%d\n", (int)return_code);
        fclose(logf);
    }

    return return_code;
}
