int32 OS_CondVarUnlock(osal_id_t var_id)
{
    OS_object_token_t token;
    int32             return_code;

    /* Try to open log file for append. Best-effort: create directory if needed. */
    FILE *log = fopen("/tmp/logs/1805.txt", "a");
    if (log == NULL)
    {
        /* Attempt to create parent directory and try again. Ignore failures. */
        (void)system("mkdir -p /tmp/logs 2>/dev/null");
        log = fopen("/tmp/logs/1805.txt", "a");
    }

    if (log)
    {
        fprintf(log, "OS_CondVarUnlock ENTER: var_id=%lu\n", (unsigned long) var_id);
        fflush(log);
    }

    /* Check Parameters */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_NONE, OS_OBJECT_TYPE_OS_CONDVAR, var_id, &token);

    if (log)
    {
        fprintf(log, "OS_ObjectIdGetById returned %d; token_addr=%p; token_size=%zu\n",
                (int)return_code, (void *)&token, (size_t)sizeof(token));
        fflush(log);
    }

    /* If successful, take a snapshot of the token bytes before and after calling the implementation.
       This will allow downstream analysis to detect whether OS_CondVarUnlock_Impl actually modifies
       the token object (evidence of whether the parameter should have been const-qualified). */
    unsigned char *pre = NULL;
    unsigned char *post = NULL;
    size_t tsize = sizeof(token);
    int modified = 0;

    if (return_code == OS_SUCCESS)
    {
        /* Allocate buffers to snapshot token contents. Use best-effort allocation. */
        pre = (unsigned char *)malloc(tsize ? tsize : 1);
        post = (unsigned char *)malloc(tsize ? tsize : 1);
        if (pre && post)
        {
            memcpy(pre, &token, tsize);
        }

        /* Call the original implementation (preserve behavior). */
        return_code = OS_CondVarUnlock_Impl(&token);

        if (pre && post)
        {
            memcpy(post, &token, tsize);
            if (memcmp(pre, post, tsize) != 0)
            {
                modified = 1;
            }
        }
    }

    if (log)
    {
        fprintf(log, "After OS_CondVarUnlock_Impl: return_code=%d\n", (int)return_code);
        if (return_code == OS_SUCCESS)
        {
            fprintf(log, "token_addr=%p modified=%d\n", (void *)&token, modified);
            if (modified && pre && post)
            {
                size_t i;
                fprintf(log, "token_diff_bytes (index: pre->post):\n");
                for (i = 0; i < tsize; ++i)
                {
                    unsigned int a = pre[i];
                    unsigned int b = post[i];
                    if (a != b)
                    {
                        fprintf(log, " %zu: 0x%02X -> 0x%02X\n", i, a, b);
                    }
                }
            }
        }

        fclose(log);
    }

    if (pre) free(pre);
    if (post) free(post);

    return return_code;
}
