int32 OS_CountSemCreate(osal_id_t *sem_id, const char *sem_name, uint32 sem_initial_value, uint32 options)
{
    int32                           return_code;
    OS_object_token_t               token;
    OS_count_sem_internal_record_t *countsem;

    /* Open log for append so nested/re-entrant calls produce ordered records */
    {
        FILE *log = fopen("/tmp/logs/2000.txt", "a");
        if (log)
        {
            fprintf(log, "ENTER OS_CountSemCreate: this=%p sem_id=%p sem_name=\"%s\" sem_initial_value=%lu options=%lu\n",
                    (void*)(&sem_id), (void*)sem_id, (sem_name ? sem_name : "(null)"), (unsigned long)sem_initial_value, (unsigned long)options);
            fflush(log);
            fclose(log);
        }
    }

    /* Check parameters */
    OS_CHECK_POINTER(sem_id);
    OS_CHECK_APINAME(sem_name);

    /* Note - the common ObjectIdAllocate routine will lock the object type and leave it locked. */
    {
        FILE *log = fopen("/tmp/logs/2000.txt", "a");
        if (log)
        {
            fprintf(log, "Calling OS_ObjectIdAllocateNew: name=\"%s\"\n", (sem_name ? sem_name : "(null)"));
            fflush(log);
            fclose(log);
        }
    }

    return_code = OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, sem_name, &token);

    {
        FILE *log = fopen("/tmp/logs/2000.txt", "a");
        if (log)
        {
            fprintf(log, "After OS_ObjectIdAllocateNew: return_code=%d token=%lu\n", (int)return_code, (unsigned long)token);
            fflush(log);
            fclose(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        countsem = OS_OBJECT_TABLE_GET(OS_count_sem_table, token);

        {
            FILE *log = fopen("/tmp/logs/2000.txt", "a");
            if (log)
            {
                fprintf(log, "OS_OBJECT_TABLE_GET -> countsem=%p for token=%lu\n", (void *)countsem, (unsigned long)token);
                fflush(log);
                fclose(log);
            }
        }

        /* Reset the table entry and save the name */
        OS_OBJECT_INIT(token, countsem, obj_name, sem_name);

        {
            FILE *log = fopen("/tmp/logs/2000.txt", "a");
            if (log)
            {
                fprintf(log, "After OS_OBJECT_INIT: token=%lu name=\"%s\"\n", (unsigned long)token, (sem_name ? sem_name : "(null)"));
                fflush(log);
                fclose(log);
            }
        }

        /* Now call the OS-specific implementation.  This reads info from the table. */
        {
            FILE *log = fopen("/tmp/logs/2000.txt", "a");
            if (log)
            {
                fprintf(log, "Calling OS_CountSemCreate_Impl for token=%lu initial=%lu options=%lu\n",
                        (unsigned long)token, (unsigned long)sem_initial_value, (unsigned long)options);
                fflush(log);
                fclose(log);
            }
        }

        return_code = OS_CountSemCreate_Impl(&token, sem_initial_value, options);

        {
            FILE *log = fopen("/tmp/logs/2000.txt", "a");
            if (log)
            {
                fprintf(log, "After OS_CountSemCreate_Impl: return_code=%d token=%lu\n", (int)return_code, (unsigned long)token);
                fflush(log);
                fclose(log);
            }
        }

        /* Check result, finalize record, and unlock global table. */
        return_code = OS_ObjectIdFinalizeNew(return_code, &token, sem_id);

        {
            FILE *log = fopen("/tmp/logs/2000.txt", "a");
            if (log)
            {
                /* sem_id is expected to be non-NULL due to OS_CHECK_POINTER above, but guard the print anyway */
                unsigned long out_id = 0UL;
                if (sem_id != NULL)
                {
                    out_id = (unsigned long)(*sem_id);
                }
                fprintf(log, "After OS_ObjectIdFinalizeNew: return_code=%d token=%lu sem_id_ptr=%p sem_id_value=%lu\n",
                        (int)return_code, (unsigned long)token, (void*)sem_id, out_id);
                fflush(log);
                fclose(log);
            }
        }
    }
    else
    {
        FILE *log = fopen("/tmp/logs/2000.txt", "a");
        if (log)
        {
            fprintf(log, "OS_ObjectIdAllocateNew failed, skipping creation: return_code=%d\n", (int)return_code);
            fflush(log);
            fclose(log);
        }
    }

    {
        FILE *log = fopen("/tmp/logs/2000.txt", "a");
        if (log)
        {
            fprintf(log, "EXIT OS_CountSemCreate: final return_code=%d\n", (int)return_code);
            fflush(log);
            fclose(log);
        }
    }

    return return_code;
}
