int32 OS_CountSemCreate(osal_id_t *sem_id, const char *sem_name, uint32 sem_initial_value, uint32 options)
{
    int32                           return_code;
    OS_object_token_t               token;
    OS_count_sem_internal_record_t *countsem;
    FILE *logf = NULL;

    /* Attempt to create log directory (best-effort) and open log file. */
    /* Use system(...) in a best-effort manner; if it is unavailable the fopen will simply fail. */
    (void)system("mkdir -p /tmp/logs 2>/dev/null");
    logf = fopen("/tmp/logs/2003.txt", "a");
    if (logf)
    {
        fprintf(logf, "OS_CountSemCreate ENTRY: sem_id=%p sem_name=%p sem_initial_value=%u options=%u\n",
                (void *)sem_id, (const void *)sem_name, (unsigned)sem_initial_value, (unsigned)options);
        if (sem_name != NULL)
        {
            /* strlen is used only for logging; guard against NULL */
            size_t name_len = strlen(sem_name);
            fprintf(logf, "  sem_name length = %zu\n", name_len);
        }
        else
        {
            fprintf(logf, "  sem_name is NULL\n");
        }
        fflush(logf);
    }

    /* Check parameters */
    OS_CHECK_POINTER(sem_id);
    OS_CHECK_APINAME(sem_name);

    /* Note - the common ObjectIdAllocate routine will lock the object type and leave it locked. */
    return_code = OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, sem_name, &token);

    if (logf)
    {
        fprintf(logf, "After OS_ObjectIdAllocateNew: return_code=%ld\n", (long)return_code);
        fflush(logf);
    }

    if (return_code == OS_SUCCESS)
    {
        countsem = OS_OBJECT_TABLE_GET(OS_count_sem_table, token);

        if (logf)
        {
            fprintf(logf, "  OS_OBJECT_TABLE_GET -> countsem=%p\n", (void *)countsem);
            fflush(logf);
        }

        /* Reset the table entry and save the name */
        OS_OBJECT_INIT(token, countsem, obj_name, sem_name);

        if (logf)
        {
            fprintf(logf, "  After OS_OBJECT_INIT: token allocated (token address printed)=%p\n", (void *)&token);
            fflush(logf);
        }

        /* Now call the OS-specific implementation.  This reads info from the table. */
        return_code = OS_CountSemCreate_Impl(&token, sem_initial_value, options);

        if (logf)
        {
            fprintf(logf, "  After OS_CountSemCreate_Impl: return_code=%ld\n", (long)return_code);
            fflush(logf);
        }

        /* Check result, finalize record, and unlock global table. */
        return_code = OS_ObjectIdFinalizeNew(return_code, &token, sem_id);

        if (logf)
        {
            fprintf(logf, "  After OS_ObjectIdFinalizeNew: return_code=%ld, final sem_id=%p\n",
                    (long)return_code, (void *)sem_id);
            fflush(logf);
        }
    }

    if (logf)
    {
        fprintf(logf, "OS_CountSemCreate EXIT: return_code=%ld\n", (long)return_code);
        fclose(logf);
    }

    return return_code;
}
