int32 OS_CountSemGetIdByName(osal_id_t *sem_id, const char *sem_name)
{
    int32 return_code;
    FILE *log = fopen("/tmp/logs/933.txt", "a");

    /* Log entry and pointer state (do not dereference sem_name to avoid introducing new UB) */
    if (log != NULL)
    {
        fprintf(log, "OS_CountSemGetIdByName: entered\n");
        fprintf(log, "  sem_id pointer = %p\n", (void *)sem_id);
        fprintf(log, "  sem_name pointer = %p\n", (const void *)sem_name);
        fclose(log);
    }

    /* Preserve original parameter checks */
    OS_CHECK_POINTER(sem_id);
    OS_CHECK_POINTER(sem_name);

    /* Log that pointers passed the checks */
    log = fopen("/tmp/logs/933.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "OS_CountSemGetIdByName: pointers passed OS_CHECK_POINTER\n");
        fclose(log);
    }

    /* Call the original lookup logic */
    return_code = OS_ObjectIdFindByName(LOCAL_OBJID_TYPE, sem_name, sem_id);

    /* Log result and current state (do not print sem_name contents to avoid additional reads) */
    log = fopen("/tmp/logs/933.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "OS_CountSemGetIdByName: OS_ObjectIdFindByName returned %d\n", (int)return_code);
        fprintf(log, "  sem_id after call = %p\n", (void *)sem_id);
        fprintf(log, "  sem_name pointer (unchanged) = %p\n", (const void *)sem_name);
        fprintf(log, "---\n");
        fclose(log);
    }

    return return_code;
}
