void OS_CreateSocketName(const OS_object_token_t *token, const OS_SockAddr_t *Addr, const char *parent_name)
{
    size_t                       len;
    uint16                       port;
    OS_stream_internal_record_t *sock;
    FILE *log = fopen("/tmp/logs/1253.txt", "a");

    if (log)
    {
        fprintf(log, "ENTER: OS_CreateSocketName token=%p Addr=%p parent_name=%p\n", (void *)token, (void *)Addr, (void *)parent_name);
    }

    sock = OS_OBJECT_TABLE_GET(OS_stream_table, *token);

    if (log)
    {
        fprintf(log, "OS_OBJECT_TABLE_GET returned sock=%p\n", (void *)sock);
        if (sock)
        {
            /* Log the raw buffer size so callers can see the maxlen passed to OS_strnlen */
            fprintf(log, "stream_name buffer sizeof(sock->stream_name)=%zu\n", sizeof(sock->stream_name));
            /* Attempt to print the current contents (may be empty or partial) */
            fprintf(log, "stream_name (before) = '%s'\n", sock->stream_name);
        }
    }

    if (OS_SocketAddrToString_Impl(sock->stream_name, sizeof(sock->stream_name), Addr) != OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "OS_SocketAddrToString_Impl FAILED for Addr=%p; zeroing first char\n", (void *)Addr);
        }
        sock->stream_name[0] = 0;
    }
    else
    {
        if (log)
        {
            fprintf(log, "OS_SocketAddrToString_Impl SUCCEEDED; stream_name (after) = '%s'\n", sock->stream_name);
        }
    }

    if (OS_SocketAddrGetPort_Impl(&port, Addr) == OS_SUCCESS)
    {
        /* NOTE: OS_strnlen may internally use memchr and read up to the provided maxlen
         * (sizeof(sock->stream_name)). This log helps reproduce scenarios where the
         * provided pointer refers to a smaller object than the maxlen, which would be
         * an out-of-bounds read. */
        if (log)
        {
            fprintf(log, "About to call OS_strnlen on stream_name with maxlen=%zu\n", sizeof(sock->stream_name));
        }

        len = OS_strnlen(sock->stream_name, sizeof(sock->stream_name));

        if (log)
        {
            fprintf(log, "OS_strnlen returned len=%zu\n", len);
            fprintf(log, "Appending port ':%u' at offset %zu (remaining buffer=%zu)\n", (unsigned)port, len, (size_t)(sizeof(sock->stream_name) - len));
        }

        snprintf(&sock->stream_name[len], sizeof(sock->stream_name) - len, ":%u", (unsigned int)port);

        if (log)
        {
            fprintf(log, "stream_name (after append port) = '%s'\n", sock->stream_name);
        }
    }

    if (parent_name)
    {
        /* Append the name from the parent socket. */
        if (log)
        {
            fprintf(log, "parent_name provided at %p; parent_name='%s'\n", (void *)parent_name, parent_name);
            fprintf(log, "About to call OS_strnlen on stream_name with maxlen=%zu (for parent append)\n", sizeof(sock->stream_name));
        }

        len = OS_strnlen(sock->stream_name, sizeof(sock->stream_name));

        if (log)
        {
            fprintf(log, "OS_strnlen returned len=%zu before parent append (remaining buffer=%zu)\n", len, (size_t)(sizeof(sock->stream_name) - len));
        }

        snprintf(&sock->stream_name[len], sizeof(sock->stream_name) - len, "-%s", parent_name);

        if (log)
        {
            fprintf(log, "stream_name (after parent append) = '%s'\n", sock->stream_name);
        }
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_CreateSocketName for token=%p\n\n", (void *)token);
        fflush(log);
        fclose(log);
    }
}
