void OS_DeleteAllObjects(void)
{
    uint32 ObjectCount;
    uint32 TryCount;
    FILE *log = fopen("/tmp/logs/1978.txt", "a");

    if (log != NULL)
    {
        fprintf(log, "OS_DeleteAllObjects: entered\n");
        fflush(log);
    }

    /*
     * Note - this is done in a loop because some objects depend on other objects
     * and you will not be able to delete the object until the ref count becomes zero.
     */
    TryCount = 0;
    while (true)
    {
        ObjectCount = 0;
        ++TryCount;

        if (log != NULL)
        {
            fprintf(log, "TryCount=%u - beginning of iteration, ObjectCount=%u\n", (unsigned)TryCount, (unsigned)ObjectCount);
            fflush(log);
        }

        /* Delete timers and tasks first, as they could be actively using other object types  */
        if (log != NULL) { fprintf(log, "Calling OS_ForEachObjectOfType: OS_OBJECT_TYPE_OS_TIMECB\n"); fflush(log); }
        OS_ForEachObjectOfType(OS_OBJECT_TYPE_OS_TIMECB, OS_OBJECT_CREATOR_ANY, OS_CleanUpObject, &ObjectCount);
        if (log != NULL) { fprintf(log, "Returned from TIMECB pass, ObjectCount=%u\n", (unsigned)ObjectCount); fflush(log); }

        if (log != NULL) { fprintf(log, "Calling OS_ForEachObjectOfType: OS_OBJECT_TYPE_OS_TIMEBASE\n"); fflush(log); }
        OS_ForEachObjectOfType(OS_OBJECT_TYPE_OS_TIMEBASE, OS_OBJECT_CREATOR_ANY, OS_CleanUpObject, &ObjectCount);
        if (log != NULL) { fprintf(log, "Returned from TIMEBASE pass, ObjectCount=%u\n", (unsigned)ObjectCount); fflush(log); }

        if (log != NULL) { fprintf(log, "Calling OS_ForEachObjectOfType: OS_OBJECT_TYPE_OS_TASK\n"); fflush(log); }
        OS_ForEachObjectOfType(OS_OBJECT_TYPE_OS_TASK, OS_OBJECT_CREATOR_ANY, OS_CleanUpObject, &ObjectCount);
        if (log != NULL) { fprintf(log, "Returned from TASK pass, ObjectCount=%u\n", (unsigned)ObjectCount); fflush(log); }

        /* Then try to delete all other remaining objects of any type */
        if (log != NULL) { fprintf(log, "Calling OS_ForEachObject (all other types)\n"); fflush(log); }
        OS_ForEachObject(OS_OBJECT_CREATOR_ANY, OS_CleanUpObject, &ObjectCount);
        if (log != NULL) { fprintf(log, "Returned from ForEachObject (all types), ObjectCount=%u\n", (unsigned)ObjectCount); fflush(log); }

        if (ObjectCount == 0 || TryCount > 4)
        {
            if (log != NULL)
            {
                fprintf(log, "Loop exit condition met: ObjectCount=%u, TryCount=%u\n", (unsigned)ObjectCount, (unsigned)TryCount);
                fflush(log);
            }
            break;
        }

        if (log != NULL)
        {
            fprintf(log, "Objects remain after iteration: ObjectCount=%u, will delay then retry (TryCount=%u)\n", (unsigned)ObjectCount, (unsigned)TryCount);
            fflush(log);
        }

        OS_TaskDelay(5);
    }

    if (log != NULL)
    {
        fprintf(log, "OS_DeleteAllObjects: exit, final TryCount=%u, final ObjectCount=%u\n", (unsigned)TryCount, (unsigned)ObjectCount);
        fclose(log);
    }
}
