int32 OS_DirRead_Impl(const OS_object_token_t *token, os_dirent_t *dirent)
{
    struct dirent *                de;
    OS_impl_dir_internal_record_t *impl;

    /* Retrieve the implementation record from the object table (same as original) */
    impl = OS_OBJECT_TABLE_GET(OS_impl_dir_table, *token);

    /* Logging probe: append information about control flow and key state to a file
     * This captures the entry into the function and the important pointers involved
     * in the scenario described (notably the non-const local pointer 'impl' and its
     * directory pointer 'dp'). The log file is /tmp/logs/715.txt.
     */
    FILE *log = fopen("/tmp/logs/715.txt", "a");
    if (log != NULL)
    {
        /* Log entry and pointer addresses. Do not dereference impl unless non-NULL. */
        fprintf(log, "OS_DirRead_Impl ENTRY: token=%p, impl=%p\n", (const void *)token, (const void *)impl);
        fprintf(log, "impl is %s\n", (impl == NULL) ? "NULL" : "non-NULL");
        /* Safe conditional access to impl->dp for logging only when impl != NULL. */
        fprintf(log, "impl->dp=%p\n", (impl != NULL) ? (void *)impl->dp : NULL);
        fflush(log);
    }

    /* NOTE - the readdir() call is non-reentrant ....
     * However, this is performed while the global dir table lock is taken.
     * Therefore this ensures that only one such call can occur at any given time.
     *
     * Static analysis tools may warn about this because they do not know
     * this function is externally serialized via the global lock.
     */
    /* cppcheck-suppress readdirCalled */
    /* cppcheck-suppress nonreentrantFunctionsreaddir */
    de = readdir(impl->dp);
    if (de == NULL)
    {
        if (log != NULL)
        {
            fprintf(log, "readdir returned NULL -> returning OS_ERROR\n");
            fclose(log);
        }
        return OS_ERROR;
    }

    if (log != NULL)
    {
        /* Log the directory entry name returned by readdir (read-only observation). */
        fprintf(log, "readdir returned de=%p d_name=\"%s\"\n", (void *)de, de->d_name ? de->d_name : "(null)");
        fflush(log);
    }

    strncpy(dirent->FileName, de->d_name, sizeof(dirent->FileName) - 1);
    dirent->FileName[sizeof(dirent->FileName) - 1] = 0;

    if (log != NULL)
    {
        fprintf(log, "Copied filename into dirent->FileName: \"%s\"\n", dirent->FileName);
        fprintf(log, "OS_DirRead_Impl EXIT: returning OS_SUCCESS\n");
        fclose(log);
    }

    return OS_SUCCESS;
}
