int32 OS_DirectoryClose(osal_id_t dir_id)
{
    OS_object_token_t token;
    int32             return_code;

    /* Open a log file to capture control flow and state. This is intentionally
     * appended so multiple calls will be recorded. If the environment does not
     * permit writing to the path, logging will be skipped gracefully. */
    FILE *log = fopen("/tmp/logs/1390.txt", "a");
    if (log)
    {
        fprintf(log, "OS_DirectoryClose ENTRY: dir_id=%u\n", (unsigned)dir_id);
        fflush(log);
    }

    /* Make sure the file descriptor is legit before using it */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_EXCLUSIVE, LOCAL_OBJID_TYPE, dir_id, &token);

    if (log)
    {
        fprintf(log, "OS_DirectoryClose: after OS_ObjectIdGetById return_code=%ld token_addr=%p\n",
                (long)return_code, (void *)&token);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "OS_DirectoryClose: calling OS_DirClose_Impl with token_addr=%p\n", (void *)&token);
            fflush(log);
        }

        return_code = OS_DirClose_Impl(&token);

        if (log)
        {
            fprintf(log, "OS_DirectoryClose: after OS_DirClose_Impl return_code=%ld\n", (long)return_code);
            fflush(log);
        }

        /* Complete the operation via the common routine */
        return_code = OS_ObjectIdFinalizeDelete(return_code, &token);

        if (log)
        {
            fprintf(log, "OS_DirectoryClose: after OS_ObjectIdFinalizeDelete final return_code=%ld\n", (long)return_code);
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "OS_DirectoryClose EXIT: dir_id=%u return_code=%ld\n", (unsigned)dir_id, (long)return_code);
        fclose(log);
    }

    return return_code;
}
