int32 OS_DirectoryRead(osal_id_t dir_id, os_dirent_t *dirent)
{
    OS_object_token_t token;
    int32             return_code;

    /* Open log file for appending; best-effort (do not fail if logging not possible) */
    FILE *log = fopen("/tmp/logs/3055.txt", "a");
    if (log)
    {
        fprintf(log, "OS_DirectoryRead ENTER: dir_id=%d, dirent=%p\n", (int)dir_id, (void *)dirent);
    }

    /* Check parameters (preserve original behavior) */
    if (log)
    {
        if (dirent == NULL)
        {
            fprintf(log, "OS_DirectoryRead: dirent == NULL (will invoke OS_CHECK_POINTER)\n");
            fflush(log);
        }
        else
        {
            /* Dump a small portion of the dirent memory (hex + ascii) to capture state before the impl call. */
            unsigned char *bytes = (unsigned char *)dirent;
            size_t i;
            fprintf(log, "OS_DirectoryRead: dirent memory BEFORE (first 32 bytes):\n  HEX:   ");
            for (i = 0; i < 32; ++i)
            {
                fprintf(log, "%02x ", (unsigned)bytes[i]);
            }
            fprintf(log, "\n  ASCII: ");
            for (i = 0; i < 32; ++i)
            {
                unsigned char c = bytes[i];
                fprintf(log, "%c", (c >= 0x20 && c < 0x7f) ? c : '.');
            }
            fprintf(log, "\n");
            fflush(log);
        }
    }

    OS_CHECK_POINTER(dirent);

    /* Make sure the file descriptor is legit before using it */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, dir_id, &token);
    if (log)
    {
        fprintf(log, "OS_DirectoryRead: OS_ObjectIdGetById returned %d\n", (int)return_code);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "OS_DirectoryRead: token acquired (%p). Calling OS_DirRead_Impl...\n", (void *)&token);
            fflush(log);
        }

        /*
         * Call the underlying implementation to perform the read
         *
         * NOTE: This does not map "virtual mount points" that
         * may appear in the dir listing back to the virtualized
         * name.  For instance, if the (real) /eeprom directory
         * is virtualized to /cf via the OS_VolumeTable, and one
         * reads the "/" directory, the application will see the
         * real name (eeprom) and not the virtualized name (cf).
         */
        return_code = OS_DirRead_Impl(&token, dirent);

        if (log)
        {
            fprintf(log, "OS_DirectoryRead: OS_DirRead_Impl returned %d\n", (int)return_code);
            /* Dump dirent memory after the impl call to capture what was written/copied into it. */
            if (dirent != NULL)
            {
                unsigned char *bytes = (unsigned char *)dirent;
                size_t i;
                fprintf(log, "OS_DirectoryRead: dirent memory AFTER (first 32 bytes):\n  HEX:   ");
                for (i = 0; i < 32; ++i)
                {
                    fprintf(log, "%02x ", (unsigned)bytes[i]);
                }
                fprintf(log, "\n  ASCII: ");
                for (i = 0; i < 32; ++i)
                {
                    unsigned char c = bytes[i];
                    fprintf(log, "%c", (c >= 0x20 && c < 0x7f) ? c : '.');
                }
                fprintf(log, "\n");
            }
            fflush(log);
        }

        OS_ObjectIdRelease(&token);

        if (log)
        {
            fprintf(log, "OS_DirectoryRead: token released\n");
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "OS_DirectoryRead EXIT: return_code=%d\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
