static int32 OS_DoSelect(int maxfd, fd_set *rd_set, fd_set *wr_set, int32 msecs)
{
    int             os_status;
    int32           return_code;
    struct timeval  tv;
    struct timeval *tvptr;
    struct timespec ts_now;
    struct timespec ts_end;

    /* Open log file for append so multiple calls are recorded */
    FILE *logf = fopen("/tmp/logs/348.txt", "a");
    if (logf)
    {
        fprintf(logf, "OS_DoSelect entry: maxfd=%d msecs=%ld\n", maxfd, (long)msecs);
        fflush(logf);
    }

    if (msecs > 0)
    {
        clock_gettime(CLOCK_MONOTONIC, &ts_now);
        ts_end.tv_sec  = ts_now.tv_sec + (msecs / 1000);
        ts_end.tv_nsec = ts_now.tv_nsec + (1000000 * (msecs % 1000));
        if (ts_end.tv_nsec >= 1000000000)
        {
            ++ts_end.tv_sec;
            ts_end.tv_nsec -= 1000000000;
        }

        if (logf)
        {
            fprintf(logf, "msecs>0 branch: ts_now=(%lld,%lld) ts_end=(%lld,%lld)\n",
                    (long long)ts_now.tv_sec, (long long)ts_now.tv_nsec,
                    (long long)ts_end.tv_sec, (long long)ts_end.tv_nsec);
            fflush(logf);
        }
    }
    else
    {
        /* Zero for consistency and to avoid possible confusion if not cleared */
        memset(&ts_end, 0, sizeof(ts_end));

        if (logf)
        {
            fprintf(logf, "msecs<=0 branch: memset(&ts_end,0) executed; ts_end=(%lld,%lld)\n",
                    (long long)ts_end.tv_sec, (long long)ts_end.tv_nsec);
            fflush(logf);
        }
    }

    do
    {
        if (msecs < 0)
        {
            tvptr = NULL;
            if (logf)
            {
                fprintf(logf, "Loop: msecs<0 -> tvptr=NULL (blocking select)\n");
                fflush(logf);
            }
        }
        else if (msecs == 0)
        {
            tvptr      = &tv;
            tv.tv_sec  = 0;
            tv.tv_usec = 0;

            if (logf)
            {
                fprintf(logf, "Loop: msecs==0 -> tvptr=&tv tv=(%ld,%ld)\n",
                        (long)tv.tv_sec, (long)tv.tv_usec);
                fflush(logf);
            }
        }
        else
        {
            tvptr = &tv;

            clock_gettime(CLOCK_MONOTONIC, &ts_now);

            /* note that the tv_sec and tv_usec/tv_nsec values are all signed longs, so OK to subtract */
            tv.tv_sec  = ts_end.tv_sec - ts_now.tv_sec;
            tv.tv_usec = (ts_end.tv_nsec - ts_now.tv_nsec) / 1000;

            if (logf)
            {
                fprintf(logf, "Loop: msecs>0 -> ts_now=(%lld,%lld) ts_end=(%lld,%lld) tv=(%ld,%ld)\n",
                        (long long)ts_now.tv_sec, (long long)ts_now.tv_nsec,
                        (long long)ts_end.tv_sec, (long long)ts_end.tv_nsec,
                        (long)tv.tv_sec, (long)tv.tv_usec);
                fflush(logf);
            }

            if (tv.tv_sec < 0 || (tv.tv_sec == 0 && tv.tv_usec < 0))
            {
                os_status = 0;
                if (logf)
                {
                    fprintf(logf, "Computed tv indicates timeout already: tv_sec=%ld tv_usec=%ld -> setting os_status=0 and breaking\n",
                            (long)tv.tv_sec, (long)tv.tv_usec);
                    fflush(logf);
                }
                break;
            }

            if (tv.tv_usec < 0)
            {
                tv.tv_usec += 1000000;
                --tv.tv_sec;

                if (logf)
                {
                    fprintf(logf, "Adjusted tv for negative usec -> tv=(%ld,%ld)\n",
                            (long)tv.tv_sec, (long)tv.tv_usec);
                    fflush(logf);
                }
            }
        }

        os_status = select(maxfd + 1, rd_set, wr_set, NULL, tvptr);

        if (logf)
        {
            fprintf(logf, "select returned os_status=%d errno=%d\n", os_status, errno);
            fflush(logf);
        }
    } while (os_status < 0 && (errno == EINTR || errno == EAGAIN));

    if (os_status < 0)
    {
        return_code = OS_ERROR;
    }
    else if (os_status == 0)
    {
        return_code = OS_ERROR_TIMEOUT;
    }
    else
    {
        return_code = OS_SUCCESS;
    }

    if (logf)
    {
        fprintf(logf, "OS_DoSelect exit: return_code=%ld\n", (long)return_code);
        fclose(logf);
    }

    return return_code;
}
