static int32 OS_DoSelect(int maxfd, fd_set *rd_set, fd_set *wr_set, int32 msecs)
{
    int             os_status;
    int32           return_code;
    struct timeval  tv;
    struct timeval *tvptr;
    struct timespec ts_now;
    struct timespec ts_end;

    /* Logging probes to help reproduce the scenario described in the report.
     * The log captures: entry parameters, computed timeout mode, snapshot of rd_set
     * before and after the select() call, select() return value and errno.
     * The log file is "/tmp/logs/345.txt".
     */
    {
        FILE *log = NULL;
        /* Try to create the logs directory if possible (ignore failures) */
        (void)mkdir("/tmp/logs", 0777);
        log = fopen("/tmp/logs/345.txt", "a");
        if (log != NULL)
        {
            time_t t = time(NULL);
            fprintf(log, "\n=== OS_DoSelect entry ===\n");
            fprintf(log, "time=%ld maxfd=%d rd_set=%p wr_set=%p msecs=%d\n", (long)t, maxfd, (void *)rd_set, (void *)wr_set, (int)msecs);
            fclose(log);
        }
    }

    if (msecs > 0)
    {
        clock_gettime(CLOCK_MONOTONIC, &ts_now);
        ts_end.tv_sec  = ts_now.tv_sec + (msecs / 1000);
        ts_end.tv_nsec = ts_now.tv_nsec + (1000000 * (msecs % 1000));
        if (ts_end.tv_nsec >= 1000000000)
        {
            ++ts_end.tv_sec;
            ts_end.tv_nsec -= 1000000000;
        }
    }
    else
    {
        /* Zero for consistency and to avoid possible confusion if not cleared */
        memset(&ts_end, 0, sizeof(ts_end));
    }

    do
    {
        if (msecs < 0)
        {
            tvptr = NULL;
        }
        else if (msecs == 0)
        {
            tvptr      = &tv;
            tv.tv_sec  = 0;
            tv.tv_usec = 0;
        }
        else
        {
            tvptr = &tv;

            clock_gettime(CLOCK_MONOTONIC, &ts_now);

            /* note that the tv_sec and tv_usec/tv_nsec values are all signed longs, so OK to subtract */
            tv.tv_sec  = ts_end.tv_sec - ts_now.tv_sec;
            tv.tv_usec = (ts_end.tv_nsec - ts_now.tv_nsec) / 1000;

            if (tv.tv_sec < 0 || (tv.tv_sec == 0 && tv.tv_usec < 0))
            {
                os_status = 0;
                break;
            }

            if (tv.tv_usec < 0)
            {
                tv.tv_usec += 1000000;
                --tv.tv_sec;
            }
        }

        /* Snapshot of rd_set before calling select so external agents can see what was passed in. */
        {
            FILE *log = NULL;
            fd_set before_sel;
            int max_probe_fd = 0;
            int i;

            if (rd_set != NULL)
            {
                /* copy contents if available */
                memcpy(&before_sel, rd_set, sizeof(fd_set));
            }

            /* Limit how many descriptors we probe to avoid huge logs; cap at 64 and FD_SETSIZE-1 and maxfd */
            max_probe_fd = maxfd;
            if (max_probe_fd >= FD_SETSIZE) max_probe_fd = FD_SETSIZE - 1;
            if (max_probe_fd > 63) max_probe_fd = 63;

            log = fopen("/tmp/logs/345.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "-- About to call select() --\n");
                if (tvptr == NULL)
                {
                    fprintf(log, "timeout: infinite (tvptr=NULL)\n");
                }
                else
                {
                    fprintf(log, "timeout: tv_sec=%ld tv_usec=%ld\n", (long)tv.tv_sec, (long)tv.tv_usec);
                }

                if (rd_set == NULL)
                {
                    fprintf(log, "rd_set: NULL\n");
                }
                else
                {
                    fprintf(log, "rd_set pointer: %p; snapshot of first %d fds before select:\n", (void *)rd_set, max_probe_fd + 1);
                    for (i = 0; i <= max_probe_fd; ++i)
                    {
                        fprintf(log, " fd %2d: %s\n", i, (FD_ISSET(i, &before_sel) ? "SET" : "clear"));
                    }
                }
                fclose(log);
            }
        }

        os_status = select(maxfd + 1, rd_set, wr_set, NULL, tvptr);
    } while (os_status < 0 && (errno == EINTR || errno == EAGAIN));

    /* Log the outcome and the rd_set after select */
    {
        FILE *log = fopen("/tmp/logs/345.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "-- select() returned: os_status=%d errno=%d (%s) --\n", os_status, errno, strerror(errno));

            if (rd_set == NULL)
            {
                fprintf(log, "rd_set after select: NULL\n");
            }
            else if (os_status > 0)
            {
                int max_probe_fd = maxfd;
                int i;
                if (max_probe_fd >= FD_SETSIZE) max_probe_fd = FD_SETSIZE - 1;
                if (max_probe_fd > 63) max_probe_fd = 63;

                fprintf(log, "rd_set pointer: %p; snapshot of first %d fds after select:\n", (void *)rd_set, max_probe_fd + 1);
                for (i = 0; i <= max_probe_fd; ++i)
                {
                    fprintf(log, " fd %2d: %s\n", i, (FD_ISSET(i, rd_set) ? "SET" : "clear"));
                }
            }
            else
            {
                fprintf(log, "rd_set after select: no descriptors (os_status=%d)\n", os_status);
            }
            fclose(log);
        }
    }

    if (os_status < 0)
    {
        return_code = OS_ERROR;
    }
    else if (os_status == 0)
    {
        return_code = OS_ERROR_TIMEOUT;
    }
    else
    {
        return_code = OS_SUCCESS;
    }

    /* Final log of return code for traceability */
    {
        FILE *log = fopen("/tmp/logs/345.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_DoSelect exit: return_code=%d\n", (int)return_code);
            fprintf(log, "=== OS_DoSelect exit ===\n");
            fclose(log);
        }
    }

    return return_code;
}
