static int32 OS_DoSelect(int maxfd, fd_set *rd_set, fd_set *wr_set, int32 msecs)
{
    int             os_status;
    int32           return_code;
    struct timeval  tv;
    struct timeval *tvptr;
    struct timespec ts_now;
    struct timespec ts_end;

    /* Logging probe: open log and record entry and initial parameters. */
    FILE *log = fopen("/tmp/logs/345.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER OS_DoSelect: maxfd=%d, rd_set=%p, wr_set=%p, msecs=%d\n", maxfd, (void *)rd_set, (void *)wr_set, msecs);
        fflush(log);
    }

    if (msecs > 0)
    {
        clock_gettime(CLOCK_MONOTONIC, &ts_now);
        ts_end.tv_sec  = ts_now.tv_sec + (msecs / 1000);
        ts_end.tv_nsec = ts_now.tv_nsec + (1000000 * (msecs % 1000));
        if (ts_end.tv_nsec >= 1000000000)
        {
            ++ts_end.tv_sec;
            ts_end.tv_nsec -= 1000000000;
        }

        if (log)
        {
            fprintf(log, "Computed ts_end: sec=%ld nsec=%ld\n", (long)ts_end.tv_sec, (long)ts_end.tv_nsec);
            fflush(log);
        }
    }
    else
    {
        /* Zero for consistency and to avoid possible confusion if not cleared */
        memset(&ts_end, 0, sizeof(ts_end));

        if (log)
        {
            fprintf(log, "ts_end zeroed (msecs <= 0)\n");
            fflush(log);
        }
    }

    /* Probe: Dump FD sets (up to a reasonable limit) to help correlate the pointer passed to other call sites. */
    if (log)
    {
        int i;
        int limit = maxfd;
#ifdef FD_SETSIZE
        if (limit >= FD_SETSIZE)
        {
            limit = FD_SETSIZE - 1;
        }
#endif
        if (limit < 0)
        {
            limit = 0;
        }

        fprintf(log, "Initial fd set contents (0..%d):\n", limit);
        for (i = 0; i <= limit; ++i)
        {
            int r = 0;
            int w = 0;
            if (rd_set != NULL)
            {
                r = FD_ISSET(i, rd_set);
            }
            if (wr_set != NULL)
            {
                w = FD_ISSET(i, wr_set);
            }
            if (r || w)
            {
                fprintf(log, "  fd %d: read=%d write=%d\n", i, r, w);
            }
        }
        fflush(log);
    }

    do
    {
        if (msecs < 0)
        {
            tvptr = NULL;

            if (log)
            {
                fprintf(log, "Loop: msecs < 0, tvptr = NULL\n");
                fflush(log);
            }
        }
        else if (msecs == 0)
        {
            tvptr      = &tv;
            tv.tv_sec  = 0;
            tv.tv_usec = 0;

            if (log)
            {
                fprintf(log, "Loop: msecs == 0, tvsec=0 tvusec=0\n");
                fflush(log);
            }
        }
        else
        {
            tvptr = &tv;

            clock_gettime(CLOCK_MONOTONIC, &ts_now);

            /* note that the tv_sec and tv_usec/tv_nsec values are all signed longs, so OK to subtract */
            tv.tv_sec  = ts_end.tv_sec - ts_now.tv_sec;
            tv.tv_usec = (ts_end.tv_nsec - ts_now.tv_nsec) / 1000;

            if (log)
            {
                fprintf(log, "Loop: computed tv before adjust: sec=%ld usec=%ld (ts_end.sec=%ld ts_end.nsec=%ld ts_now.sec=%ld ts_now.nsec=%ld)\n",
                        (long)tv.tv_sec, (long)tv.tv_usec,
                        (long)ts_end.tv_sec, (long)ts_end.tv_nsec,
                        (long)ts_now.tv_sec, (long)ts_now.tv_nsec);
                fflush(log);
            }

            if (tv.tv_sec < 0 || (tv.tv_sec == 0 && tv.tv_usec < 0))
            {
                os_status = 0;

                if (log)
                {
                    fprintf(log, "Timeout already expired: tv.tv_sec=%ld tv.tv_usec=%ld -> os_status=0 break\n", (long)tv.tv_sec, (long)tv.tv_usec);
                    fflush(log);
                }

                break;
            }

            if (tv.tv_usec < 0)
            {
                tv.tv_usec += 1000000;
                --tv.tv_sec;

                if (log)
                {
                    fprintf(log, "Adjusted tv: sec=%ld usec=%ld\n", (long)tv.tv_sec, (long)tv.tv_usec);
                    fflush(log);
                }
            }
        }

        if (log)
        {
            if (tvptr == NULL)
            {
                fprintf(log, "Calling select with tvptr=NULL, maxfd+1=%d\n", maxfd + 1);
            }
            else
            {
                fprintf(log, "Calling select with tv: sec=%ld usec=%ld, maxfd+1=%d\n", (long)tv.tv_sec, (long)tv.tv_usec, maxfd + 1);
            }
            fflush(log);
        }

        os_status = select(maxfd + 1, rd_set, wr_set, NULL, tvptr);

        if (log)
        {
            int saved_errno = errno;
            fprintf(log, "select returned os_status=%d errno=%d\n", os_status, saved_errno);
            fflush(log);
        }
    } while (os_status < 0 && (errno == EINTR || errno == EAGAIN));

    if (os_status < 0)
    {
        return_code = OS_ERROR;

        if (log)
        {
            fprintf(log, "Result: OS_ERROR (os_status=%d errno=%d)\n", os_status, errno);
            fflush(log);
        }
    }
    else if (os_status == 0)
    {
        return_code = OS_ERROR_TIMEOUT;

        if (log)
        {
            fprintf(log, "Result: OS_ERROR_TIMEOUT (os_status==0)\n");
            fflush(log);
        }
    }
    else
    {
        return_code = OS_SUCCESS;

        if (log)
        {
            fprintf(log, "Result: OS_SUCCESS (os_status=%d)\n", os_status);
            fflush(log);

            /* Also dump which descriptors are marked now so callers can correlate addresses and contents. */
            int i;
#ifdef FD_SETSIZE
            int limit = (maxfd >= FD_SETSIZE) ? (FD_SETSIZE - 1) : maxfd;
#else
            int limit = maxfd;
#endif
            if (limit < 0)
            {
                limit = 0;
            }
            fprintf(log, "Post-select fd set contents (0..%d):\n", limit);
            for (i = 0; i <= limit; ++i)
            {
                int r = 0;
                int w = 0;
                if (rd_set != NULL)
                {
                    r = FD_ISSET(i, rd_set);
                }
                if (wr_set != NULL)
                {
                    w = FD_ISSET(i, wr_set);
                }
                if (r || w)
                {
                    fprintf(log, "  fd %d: read=%d write=%d\n", i, r, w);
                }
            }
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "EXIT OS_DoSelect: return_code=%d\n\n", return_code);
        fclose(log);
    }

    return return_code;
}
