static int32 OS_DoTimerAdd(osal_id_t *timer_id, const char *timer_name, osal_id_t timebase_ref_id,
                           OS_ArgCallback_t callback_ptr, void *callback_arg, uint32 flags)
{
    int32                          return_code;
    osal_objtype_t                 objtype;
    OS_object_token_t              timebase_token;
    OS_object_token_t              timecb_token;
    OS_object_token_t              listcb_token;
    OS_timecb_internal_record_t *  timecb;
    OS_timecb_internal_record_t *  list_timecb;
    OS_timebase_internal_record_t *timebase;

    /* Open log for tracing this function's control flow and state. Use append mode so multiple calls accumulate. */
    FILE *log = fopen("/tmp/logs/375.txt", "a");
    int have_log = (log != NULL);
    if (have_log)
    {
        fprintf(log, "ENTER OS_DoTimerAdd: timer_id=%p name=%s timebase_ref_id=%u callback_ptr=%p callback_arg=%p flags=%u\n",
                (void *)timer_id, (timer_name ? timer_name : "(null)"), (unsigned)timebase_ref_id, (void *)callback_ptr,
                callback_arg, (unsigned)flags);
        fflush(log);
    }

    /*
     * Check parameters
     *
     * Note "callback_arg" is not checked, because in certain configurations it can be validly null.
     */
    OS_CHECK_POINTER(timer_id);
    OS_CHECK_APINAME(timer_name);
    OS_CHECK_POINTER(callback_ptr);

    /*
     * Check our context.  Not allowed to use the timer API from a timer callback.
     * Just interested in the object type returned.
     */
    objtype = OS_ObjectIdToType_Impl(OS_TaskGetId_Impl());
    if (have_log)
    {
        fprintf(log, "After TaskGetId: objtype=%u\n", (unsigned)objtype);
        fflush(log);
    }
    if (objtype == OS_OBJECT_TYPE_OS_TIMEBASE)
    {
        if (have_log)
        {
            fprintf(log, "ERROR: incorrect object state (called from timebase) -> returning %d\n", OS_ERR_INCORRECT_OBJ_STATE);
            fclose(log);
        }
        return OS_ERR_INCORRECT_OBJ_STATE;
    }

    /*
     * Check that the timebase reference is valid
     * If successful, then after this statement, we MUST decrement the refcount
     * if we leave this routine with an error.
     */
    return_code =
        OS_ObjectIdGetById(OS_LOCK_MODE_REFCOUNT, OS_OBJECT_TYPE_OS_TIMEBASE, timebase_ref_id, &timebase_token);
    if (have_log)
    {
        fprintf(log, "OS_ObjectIdGetById(timebase): return_code=%d\n", (int)return_code);
        fflush(log);
    }
    if (return_code != OS_SUCCESS)
    {
        if (have_log)
        {
            fprintf(log, "EXIT OS_DoTimerAdd: timebase lookup failed -> return %d\n", (int)return_code);
            fclose(log);
        }
        return return_code;
    }

    /* Note - the common ObjectIdAllocate routine will lock the object type and leave it locked. */
    return_code = OS_ObjectIdAllocateNew(OS_OBJECT_TYPE_OS_TIMECB, timer_name, &timecb_token);
    if (have_log)
    {
        fprintf(log, "OS_ObjectIdAllocateNew -> return_code=%d\n", (int)return_code);
        fflush(log);
    }
    if (return_code == OS_SUCCESS)
    {
        timecb   = OS_OBJECT_TABLE_GET(OS_timecb_table, timecb_token);
        timebase = OS_OBJECT_TABLE_GET(OS_timebase_table, timebase_token);

        /* Reset the table entry and save the name */
        OS_OBJECT_INIT(timecb_token, timecb, timer_name, timer_name);

        /*
         * transfer ownership so the refcount obtained earlier is now
         * associated with the timecb object, and will be retained until
         * the object is deleted.
         */
        OS_ObjectIdTransferToken(&timebase_token, &timecb->timebase_token);

        /* Log the pointers/values being stored so we can detect if callback_arg is only forwarded and never dereferenced */
        timecb->callback_ptr = callback_ptr;
        timecb->callback_arg = callback_arg;
        timecb->flags        = flags;
        timecb->prev_cb      = OS_ObjectIdFromToken(&timecb_token);
        timecb->next_cb      = OS_ObjectIdFromToken(&timecb_token);

        if (have_log)
        {
            fprintf(log, "Assigned timecb fields: timecb=%p callback_ptr=%p callback_arg=%p flags=%u prev_cb=%u next_cb=%u\n",
                    (void *)timecb, (void *)timecb->callback_ptr, timecb->callback_arg, (unsigned)timecb->flags,
                    (unsigned)timecb->prev_cb, (unsigned)timecb->next_cb);
            fflush(log);
        }

        /*
         * Now we need to add it to the time base callback ring, so take the
         * timebase-specific lock to prevent a tick from being processed at this moment.
         */
        if (have_log)
        {
            fprintf(log, "Acquiring timebase lock for timebase_token...\n");
            fflush(log);
        }
        OS_TimeBaseLock_Impl(&timebase_token);
        if (have_log)
        {
            fprintf(log, "Timebase lock acquired. timebase=%p\n", (void *)timebase);
            fflush(log);
        }

        if (OS_ObjectIdGetById(OS_LOCK_MODE_NONE, OS_OBJECT_TYPE_OS_TIMECB, timebase->first_cb, &listcb_token) ==
            OS_SUCCESS)
        {
            list_timecb = OS_OBJECT_TABLE_GET(OS_timecb_table, listcb_token);

            if (have_log)
            {
                fprintf(log, "Inserting into existing callback ring: first_cb=%u list_timecb=%p\n", (unsigned)timebase->first_cb,
                        (void *)list_timecb);
                fflush(log);
            }

            timecb->next_cb = OS_ObjectIdFromToken(&listcb_token);
            timecb->prev_cb = list_timecb->prev_cb;

            if (OS_ObjectIdGetById(OS_LOCK_MODE_NONE, OS_OBJECT_TYPE_OS_TIMECB, timecb->prev_cb, &listcb_token) ==
                OS_SUCCESS)
            {
                list_timecb->prev_cb = OS_ObjectIdFromToken(&timecb_token);
                list_timecb          = OS_OBJECT_TABLE_GET(OS_timecb_table, listcb_token);
                list_timecb->next_cb = OS_ObjectIdFromToken(&timecb_token);

                if (have_log)
                {
                    fprintf(log, "Linked into ring: new prev=%u new next=%u updated list_timecb=%p\n",
                            (unsigned)timecb->prev_cb, (unsigned)timecb->next_cb, (void *)list_timecb);
                    fflush(log);
                }
            }
            else if (have_log)
            {
                fprintf(log, "Warning: failed to get previous callback in ring for id=%u\n", (unsigned)timecb->prev_cb);
                fflush(log);
            }
        }
        else if (have_log)
        {
            fprintf(log, "No existing callbacks on timebase (or lookup failed) - first_cb=%u\n", (unsigned)timebase->first_cb);
            fflush(log);
        }

        timebase->first_cb = OS_ObjectIdFromToken(&timecb_token);

        OS_TimeBaseUnlock_Impl(&timebase_token);
        if (have_log)
        {
            fprintf(log, "Released timebase lock. Set timebase->first_cb=%u\n", (unsigned)timebase->first_cb);
            fflush(log);
        }

        /* Check result, finalize record, and unlock global table. */
        return_code = OS_ObjectIdFinalizeNew(return_code, &timecb_token, timer_id);
        if (have_log)
        {
            fprintf(log, "Finalize new timecb -> return_code=%d timer_id=%p\n", (int)return_code, (void *)timer_id);
            fclose(log);
        }
    }
    else
    {
        if (have_log)
        {
            fprintf(log, "Allocation failed for new timercb -> releasing timebase_token and returning %d\n", (int)return_code);
            fflush(log);
        }
        OS_ObjectIdRelease(&timebase_token);
        if (have_log)
        {
            fclose(log);
        }
    }

    return return_code;
}
