int32 OS_FDGetInfo(osal_id_t filedes, OS_file_prop_t *fd_prop)
{
    OS_common_record_t *record;
    OS_object_token_t   token;
    int32               return_code;

    /* Logging probe: open log file to capture control flow and state */
    FILE *os_fd_log = fopen("/tmp/logs/1093.txt", "a");
    if (os_fd_log)
    {
        fprintf(os_fd_log, "OS_FDGetInfo ENTER: filedes=%d, fd_prop=%p\n", (int)filedes, (void *)fd_prop);
    }

    /* Check parameters */
    OS_CHECK_POINTER(fd_prop);
    if (os_fd_log) fprintf(os_fd_log, "OS_CHECK_POINTER passed\n");

    memset(fd_prop, 0, sizeof(OS_file_prop_t));
    if (os_fd_log) fprintf(os_fd_log, "fd_prop zeroed (size=%zu)\n", sizeof(OS_file_prop_t));

    /* Check Parameters */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, filedes, &token);
    if (os_fd_log) fprintf(os_fd_log, "OS_ObjectIdGetById returned %d\n", return_code);

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_stream_table, token);
        if (os_fd_log) fprintf(os_fd_log, "record=%p\n", (void *)record);

        if (record->name_entry != NULL)
        {
            /* Log the pointers and sizes involved in the copy operation. */
            size_t copy_len = sizeof(fd_prop->Path) - 1;
            if (os_fd_log)
            {
                fprintf(os_fd_log, "record->name_entry=%p, fd_prop->Path=%p, sizeof(fd_prop->Path)=%zu, requested_copy_len=%zu\n",
                        (void *)record->name_entry, (void *)fd_prop->Path, sizeof(fd_prop->Path), copy_len);
                fflush(os_fd_log);
            }

            /* Preserve original behavior: perform the same strncpy as in the target code. */
            strncpy(fd_prop->Path, record->name_entry, copy_len);

            /* After the copy, log the resulting Path buffer (safe because fd_prop was zeroed earlier).
             * fd_prop->Path is NUL-terminated if the source fit, or contains the first copy_len bytes otherwise.
             */
            if (os_fd_log)
            {
                fprintf(os_fd_log, "strncpy completed, fd_prop->Path='%s'\n", fd_prop->Path);
            }
        }
        else
        {
            if (os_fd_log) fprintf(os_fd_log, "record->name_entry is NULL, skipping strncpy\n");
        }

        fd_prop->User    = record->creator;
        fd_prop->IsValid = true;

        if (os_fd_log) fprintf(os_fd_log, "fd_prop->User=%lu, fd_prop->IsValid=%d\n", (unsigned long)fd_prop->User, (int)fd_prop->IsValid);

        OS_ObjectIdRelease(&token);
        if (os_fd_log) fprintf(os_fd_log, "OS_ObjectIdRelease called\n");
    }

    if (os_fd_log)
    {
        fprintf(os_fd_log, "OS_FDGetInfo EXIT: return_code=%d\n", return_code);
        fclose(os_fd_log);
    }

    return return_code;
}
