int32 OS_FDGetInfo(osal_id_t filedes, OS_file_prop_t *fd_prop)
{
    OS_common_record_t *record;
    OS_object_token_t   token;
    int32               return_code;
    FILE *log = fopen("/tmp/logs/1095.txt", "a");

    /* Check parameters */
    OS_CHECK_POINTER(fd_prop);

    if (log)
    {
        fprintf(log, "OS_FDGetInfo: entered filedes=%d fd_prop=%p\n", (int)filedes, (void *)fd_prop);
        fflush(log);
    }

    memset(fd_prop, 0, sizeof(OS_file_prop_t));
    if (log)
    {
        fprintf(log, "OS_FDGetInfo: fd_prop zeroed\n");
        fflush(log);
    }

    /* Check Parameters */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, filedes, &token);
    if (log)
    {
        fprintf(log, "OS_FDGetInfo: OS_ObjectIdGetById returned %ld\n", (long)return_code);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_stream_table, token);
        if (log)
        {
            fprintf(log, "OS_FDGetInfo: record=%p\n", (void *)record);
            fflush(log);
        }

        if (record->name_entry != NULL)
        {
            if (log)
            {
                fprintf(log, "OS_FDGetInfo: name_entry='%s'\n", record->name_entry);
                fflush(log);
            }
            strncpy(fd_prop->Path, record->name_entry, sizeof(fd_prop->Path) - 1);
        }
        else
        {
            if (log)
            {
                fprintf(log, "OS_FDGetInfo: name_entry is NULL\n");
                fflush(log);
            }
        }

        fd_prop->User    = record->creator;
        fd_prop->IsValid = true;

        if (log)
        {
            fprintf(log, "OS_FDGetInfo: User=%u IsValid=%d\n", (unsigned)fd_prop->User, (int)fd_prop->IsValid);
            fflush(log);
        }

        OS_ObjectIdRelease(&token);

        if (log)
        {
            fprintf(log, "OS_FDGetInfo: token released\n");
            fflush(log);
        }
    }
    else
    {
        if (log)
        {
            fprintf(log, "OS_FDGetInfo: object id not found, returning %ld\n", (long)return_code);
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "OS_FDGetInfo: exit return_code=%ld\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
