int32 OS_FDGetInfo(osal_id_t filedes, OS_file_prop_t *fd_prop)
{
    OS_common_record_t *record;
    OS_object_token_t   token;
    int32               return_code;

    /* Minimal entry log to prove function is reached and record input state */
    {
        FILE *log = fopen("/tmp/logs/1095.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "ENTER OS_FDGetInfo: filedes=%lu, fd_prop=%p\n", (unsigned long)filedes, (void *)fd_prop);
            fclose(log);
        }
    }

    /* Check parameters */
    OS_CHECK_POINTER(fd_prop);

    memset(fd_prop, 0, sizeof(OS_file_prop_t));

    /* Check Parameters */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, filedes, &token);

    {
        FILE *log = fopen("/tmp/logs/1095.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_ObjectIdGetById returned %ld\n", (long)return_code);
            fclose(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_stream_table, token);

        {
            FILE *log = fopen("/tmp/logs/1095.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "Lookup record at %p\n", (void *)record);
                fclose(log);
            }
        }

        if (record->name_entry != NULL)
        {
            /* Log the name to capture the read-only usage of the table's contents */
            {
                FILE *log = fopen("/tmp/logs/1095.txt", "a");
                if (log != NULL)
                {
                    fprintf(log, "record->name_entry = '%s'\n", record->name_entry);
                    fclose(log);
                }
            }

            strncpy(fd_prop->Path, record->name_entry, sizeof(fd_prop->Path) - 1);

            {
                FILE *log = fopen("/tmp/logs/1095.txt", "a");
                if (log != NULL)
                {
                    fprintf(log, "Copied Path -> fd_prop->Path = '%s'\n", fd_prop->Path);
                    fclose(log);
                }
            }
        }

        /* Log creator and assignment to demonstrate only read access */
        {
            FILE *log = fopen("/tmp/logs/1095.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "record->creator = %lu\n", (unsigned long)record->creator);
                fclose(log);
            }
        }

        fd_prop->User    = record->creator;
        fd_prop->IsValid = true;

        {
            FILE *log = fopen("/tmp/logs/1095.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "Set fd_prop->User=%lu, fd_prop->IsValid=%d\n", (unsigned long)fd_prop->User, fd_prop->IsValid ? 1 : 0);
                fclose(log);
            }
        }

        OS_ObjectIdRelease(&token);

        {
            FILE *log = fopen("/tmp/logs/1095.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "Released token and about to return OS_SUCCESS\n");
                fclose(log);
            }
        }
    }
    else
    {
        /* Log unsuccessful lookup */
        FILE *log = fopen("/tmp/logs/1095.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_FDGetInfo: object id lookup failed with code %ld\n", (long)return_code);
            fclose(log);
        }
    }

    return return_code;
}
