int32 OS_FDGetInfo(osal_id_t filedes, OS_file_prop_t *fd_prop)
{
    OS_common_record_t *record;
    OS_object_token_t   token;
    int32               return_code;
    FILE *logf = NULL;

    /* Check parameters */
    OS_CHECK_POINTER(fd_prop);

    /* Open log for tracing control flow and state (append so multiple calls accumulate) */
    logf = fopen("/tmp/logs/1095.txt", "a");
    if (logf)
    {
        fprintf(logf, "OS_FDGetInfo ENTER: filedes=%ld\n", (long)filedes);
    }

    memset(fd_prop, 0, sizeof(OS_file_prop_t));

    /* Check Parameters */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, filedes, &token);
    if (logf)
    {
        fprintf(logf, "OS_ObjectIdGetById returned: %ld\n", (long)return_code);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_stream_table, token);

        if (logf)
        {
            /* Log token address and record pointer (do not dereference record unless non-NULL) */
            fprintf(logf, "token addr=%p record ptr=%p\n", (void*)&token, (void*)record);
            if (record != NULL)
            {
                fprintf(logf, "record->name_entry=%p record->creator=%lu\n",
                        (void*)record->name_entry, (unsigned long)record->creator);
            }
            else
            {
                fprintf(logf, "record is NULL\n");
            }
        }

        if (record->name_entry != NULL)
        {
            strncpy(fd_prop->Path, record->name_entry, sizeof(fd_prop->Path) - 1);
            if (logf)
            {
                fprintf(logf, "Copied name_entry to fd_prop->Path (%s)\n", fd_prop->Path);
            }
        }
        else if (logf)
        {
            fprintf(logf, "record->name_entry is NULL\n");
        }

        fd_prop->User    = record->creator;
        fd_prop->IsValid = true;

        if (logf)
        {
            fprintf(logf, "Set fd_prop->User=%lu fd_prop->IsValid=%d\n",
                    (unsigned long)fd_prop->User, (int)fd_prop->IsValid);
        }

        OS_ObjectIdRelease(&token);

        if (logf)
        {
            fprintf(logf, "Released token\n");
        }
    }

    if (logf)
    {
        fprintf(logf, "OS_FDGetInfo EXIT: return_code=%ld\n\n", (long)return_code);
        fclose(logf);
    }

    return return_code;
}
