int32 OS_FS_GetPhysDriveName(char *PhysDriveName, const char *MountPoint)
{
    OS_object_token_t             token;
    int32                         return_code;
    OS_filesys_internal_record_t *filesys;

    /* Minimal file-based tracing to capture control flow and state for debugging */
    {
        FILE *log = fopen("/tmp/logs/850.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "ENTER: OS_FS_GetPhysDriveName(PhysDriveName=%p, MountPoint=%p)\n",
                    (void *)PhysDriveName, (const void *)MountPoint);
            fclose(log);
        }
    }

    /* Check parameters (preserve original behavior) */
    OS_CHECK_PATHNAME(MountPoint);
    OS_CHECK_POINTER(PhysDriveName);

    /* Log that parameter checks completed (this is useful to see if CHECK macros invoked logging/printing) */
    {
        FILE *log = fopen("/tmp/logs/850.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "AFTER_CHECKS: MountPoint=%p PhysDriveName=%p\n",
                    (const void *)MountPoint, (void *)PhysDriveName);
            fclose(log);
        }
    }

    /* Get a reference lock, as a filesystem check could take some time. */
    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)MountPoint, &token);

    {
        FILE *log = fopen("/tmp/logs/850.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_ObjectIdGetBySearch returned %ld\n", (long)return_code);
            /* Attempt to log token information in a generic, safe way */
            fprintf(log, "TOKEN_ADDR=%p\n", (void *)&token);
            fclose(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        {
            FILE *log = fopen("/tmp/logs/850.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "FILESYS_PTR=%p\n", (void *)filesys);
                if (filesys != NULL)
                {
                    fprintf(log, "FILESYS_FLAGS=0x%08lx system_mountpt=%s\n",
                            (unsigned long)filesys->flags,
                            filesys->system_mountpt ? filesys->system_mountpt : "(null)");
                }
                fclose(log);
            }
        }

        if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM) != 0)
        {
            /* Log the copy action and final physical name */
            strncpy(PhysDriveName, filesys->system_mountpt, OS_FS_PHYS_NAME_LEN - 1);
            PhysDriveName[OS_FS_PHYS_NAME_LEN - 1] = 0;

            {
                FILE *log = fopen("/tmp/logs/850.txt", "a");
                if (log != NULL)
                {
                    fprintf(log, "COPIED_PHYSNAME=%s\n", PhysDriveName);
                    fclose(log);
                }
            }
        }
        else
        {
            return_code = OS_ERR_INCORRECT_OBJ_STATE;

            {
                FILE *log = fopen("/tmp/logs/850.txt", "a");
                if (log != NULL)
                {
                    fprintf(log, "ERR: incorrect object state (flags=0x%08lx) -> return_code=%ld\n",
                            (unsigned long)filesys->flags, (long)return_code);
                    fclose(log);
                }
            }
        }

        OS_ObjectIdRelease(&token);

        {
            FILE *log = fopen("/tmp/logs/850.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "AFTER_RELEASE: return_code=%ld\n", (long)return_code);
                fclose(log);
            }
        }
    }
    else
    {
        return_code = OS_ERR_NAME_NOT_FOUND;

        {
            FILE *log = fopen("/tmp/logs/850.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "ERR: name not found -> returning %ld\n", (long)return_code);
                fclose(log);
            }
        }
    }

    {
        FILE *log = fopen("/tmp/logs/850.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "EXIT: OS_FS_GetPhysDriveName -> return_code=%ld\n", (long)return_code);
            fclose(log);
        }
    }

    return return_code;
}
