static int32 OS_FdSet_ConvertIn_Impl(int *os_maxfd, fd_set *os_set, const OS_FdSet *OSAL_set)
{
    size_t       offset;
    size_t       bit;
    osal_index_t id;
    uint8        objids;
    int          osfd;
    int32        status;

    /* Open a log file to capture control flow and state for reproduction/debugging */
    {
        FILE *log = fopen("/tmp/logs/1829.txt", "a");
        if (log != NULL)
        {
            size_t arr_size = sizeof(OSAL_set->object_ids);
            fprintf(log, "ENTER OS_FdSet_ConvertIn_Impl: os_maxfd=%p (*os_maxfd=%d), os_set=%p, OSAL_set=%p, sizeof(object_ids)=%zu\n",
                    (void *)os_maxfd, (os_maxfd ? *os_maxfd : -1), (void *)os_set, (void *)OSAL_set, arr_size);
            fclose(log);
        }
    }

    status = OS_SUCCESS;
    for (offset = 0; offset < sizeof(OSAL_set->object_ids); ++offset)
    {
        objids = OSAL_set->object_ids[offset];

        /* Log the offset and initial objids value for each byte processed */
        {
            FILE *log = fopen("/tmp/logs/1829.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "LOOP offset=%zu: initial objids=0x%02x\n", offset, (unsigned int)objids);
                fclose(log);
            }
        }

        bit    = 0;
        while (objids != 0)
        {
            id = OSAL_INDEX_C((offset * 8) + bit);

            /* Log each bit iteration and computed id */
            {
                FILE *log = fopen("/tmp/logs/1829.txt", "a");
                if (log != NULL)
                {
                    fprintf(log, "  BIT bit=%zu: remaining objids=0x%02x, id=%llu\n",
                            bit, (unsigned int)objids, (unsigned long long)id);
                    fclose(log);
                }
            }

            if ((objids & 0x01) != 0 && id < OS_MAX_NUM_OPEN_FILES)
            {
                osfd = OS_impl_filehandle_table[id].fd;

                /* Log the resolved osfd and selectable flag */
                {
                    FILE *log = fopen("/tmp/logs/1829.txt", "a");
                    if (log != NULL)
                    {
                        fprintf(log, "    CHECK id=%llu -> osfd=%d, selectable=%d\n",
                                (unsigned long long)id, osfd, (int)OS_impl_filehandle_table[id].selectable);
                        fclose(log);
                    }
                }

                if (osfd >= 0)
                {
                    if (osfd >= FD_SETSIZE || !OS_impl_filehandle_table[id].selectable)
                    {
                        /* out of range of select() implementation */
                        status = OS_ERR_OPERATION_NOT_SUPPORTED;

                        /* Log the error condition */
                        {
                            FILE *log = fopen("/tmp/logs/1829.txt", "a");
                            if (log != NULL)
                            {
                                fprintf(log, "    ERROR id=%llu -> osfd=%d out of range or not selectable, status=%ld\n",
                                        (unsigned long long)id, osfd, (long)status);
                                fclose(log);
                            }
                        }
                    }
                    else
                    {
                        FD_SET(osfd, os_set);
                        if (osfd > *os_maxfd)
                        {
                            *os_maxfd = osfd;

                            /* Log the update to max fd */
                            FILE *log = fopen("/tmp/logs/1829.txt", "a");
                            if (log != NULL)
                            {
                                fprintf(log, "    FD_SET id=%llu -> osfd=%d (new maxfd=%d)\n",
                                        (unsigned long long)id, osfd, *os_maxfd);
                                fclose(log);
                            }
                        }
                        else
                        {
                            FILE *log = fopen("/tmp/logs/1829.txt", "a");
                            if (log != NULL)
                            {
                                fprintf(log, "    FD_SET id=%llu -> osfd=%d (maxfd remains=%d)\n",
                                        (unsigned long long)id, osfd, *os_maxfd);
                                fclose(log);
                            }
                        }
                    }
                }
            }

            ++bit;
            objids >>= 1;
        }
    }

    /* Final status log */
    {
        FILE *log = fopen("/tmp/logs/1829.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "EXIT OS_FdSet_ConvertIn_Impl: status=%ld, final *os_maxfd=%d\n", (long)status, (os_maxfd ? *os_maxfd : -1));
            fclose(log);
        }
    }

    return status;
}
