static void OS_FdSet_ConvertOut_Impl(fd_set *OS_set, OS_FdSet *OSAL_set)
{
    size_t       offset;
    size_t       bit;
    osal_index_t id;
    uint8        objids;
    int          osfd;

    /* Open log file for appending; best-effort (may be NULL if open fails) */
    FILE *log = fopen("/tmp/logs/2111.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER OS_FdSet_ConvertOut_Impl: OS_set=%p OSAL_set=%p\n", (void *)OS_set, (void *)OSAL_set);
        fflush(log);
    }

    /* Preserve original loop header (keeps the syntactic use of OSAL_set->object_ids in the second clause) */
    for (offset = 0; offset < sizeof(OSAL_set->object_ids); ++offset)
    {
        if (log != NULL)
        {
            fprintf(log, "LOOP offset=%zu initial_object_ids=0x%02x\n", offset,
                    (unsigned)OSAL_set->object_ids[offset]);
            fflush(log);
        }

        objids = OSAL_set->object_ids[offset];
        bit    = 0;
        while (objids != 0)
        {
            id = OSAL_INDEX_C((offset * 8) + bit);

            if (log != NULL)
            {
                fprintf(log, "  WHILE offset=%zu bit=%zu objids=0x%02x id=%u\n",
                        offset, bit, (unsigned)objids, (unsigned)id);
                fflush(log);
            }

            if ((objids & 0x01) != 0 && id < OS_MAX_NUM_OPEN_FILES)
            {
                osfd = OS_impl_filehandle_table[id].fd;

                /* Evaluate FD_ISSET once for logging and decision; preserve original semantics */
                int fd_is_set = (osfd >= 0) ? FD_ISSET(osfd, OS_set) : 0;

                if (log != NULL)
                {
                    fprintf(log, "    CHECK id=%u osfd=%d FD_ISSET=%d\n", (unsigned)id, osfd, fd_is_set);
                    fflush(log);
                }

                if (osfd < 0 || !fd_is_set)
                {
                    if (log != NULL)
                    {
                        fprintf(log, "    CLEARING bit=%zu at offset=%zu (before=0x%02x)\n", bit, offset,
                                (unsigned)OSAL_set->object_ids[offset]);
                        fflush(log);
                    }

                    /* Original modification preserved */
                    OSAL_set->object_ids[offset] &= ~(1u << bit);

                    if (log != NULL)
                    {
                        fprintf(log, "    CLEARED bit=%zu at offset=%zu (after=0x%02x)\n", bit, offset,
                                (unsigned)OSAL_set->object_ids[offset]);
                        fflush(log);
                    }
                }
            }

            ++bit;
            objids >>= 1;
        }

        if (log != NULL)
        {
            fprintf(log, "LOOP_END offset=%zu final_object_ids=0x%02x\n", offset,
                    (unsigned)OSAL_set->object_ids[offset]);
            fflush(log);
        }
    }

    if (log != NULL)
    {
        fprintf(log, "EXIT OS_FdSet_ConvertOut_Impl\n");
        fclose(log);
    }
}
