static void OS_FdSet_ConvertOut_Impl(fd_set *OS_set, OS_FdSet *OSAL_set)
{
    size_t       offset;
    size_t       bit;
    osal_index_t id;
    uint8        objids;
    int          osfd;

    /* Open log file (append) to record control flow and state. */
    FILE *log = fopen("/tmp/logs/2115.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "OS_FdSet_ConvertOut_Impl: entry OS_set=%p OSAL_set=%p\n", (void *)OS_set, (void *)OSAL_set);
    }

    for (offset = 0; offset < sizeof(OSAL_set->object_ids); ++offset)
    {
        objids = OSAL_set->object_ids[offset];
        if (log != NULL)
        {
            fprintf(log, " offset %zu: init objids=0x%02x\n", offset, (unsigned)objids);
        }

        bit = 0;
        while (objids != 0)
        {
            id = OSAL_INDEX_C((offset * 8) + bit);
            if ((objids & 0x01) != 0 && id < OS_MAX_NUM_OPEN_FILES)
            {
                osfd = OS_impl_filehandle_table[id].fd;

                /* Evaluate FD membership once for logging, but preserve original logic. */
                int isset = (osfd >= 0 && FD_ISSET(osfd, OS_set));

                if (log != NULL)
                {
                    fprintf(log, "  bit %zu id=%zu osfd=%d FD_ISSET=%d\n", bit, (size_t)id, osfd, isset);
                }

                if (osfd < 0 || !isset)
                {
                    /* Clear the bit in the OSAL set just like original implementation. */
                    OSAL_set->object_ids[offset] &= ~(1 << bit);
                    if (log != NULL)
                    {
                        fprintf(log, "   -> cleared bit %zu; new objids=0x%02x\n", bit, (unsigned)OSAL_set->object_ids[offset]);
                    }
                }
            }

            ++bit;
            objids >>= 1;
        }
    }

    if (log != NULL)
    {
        fprintf(log, "OS_FdSet_ConvertOut_Impl: exit\n");
        fclose(log);
    }
}
