static void OS_FdSet_ConvertOut_Impl(fd_set *OS_set, OS_FdSet *OSAL_set)
{
    FILE        *log;
    size_t       offset;
    size_t       bit;
    osal_index_t id;
    uint8        objids;
    int          osfd;

    /* Open log file (append). If unable to open, fall back to stderr so tests still run. */
    log = fopen("/tmp/logs/2115.txt", "a");
    if (log == NULL)
    {
        log = stderr;
    }

    fprintf(log, "ENTER OS_FdSet_ConvertOut_Impl: OS_set=%p, OSAL_set=%p\n", (void *)OS_set, (void *)OSAL_set);
    fprintf(log, "object_ids array size = %zu\n", sizeof(OSAL_set->object_ids));

    /* Dump initial object_ids bytes for traceability */
    for (offset = 0; offset < sizeof(OSAL_set->object_ids); ++offset)
    {
        fprintf(log, "initial object_ids[%zu]=0x%02X\n", offset, (unsigned)OSAL_set->object_ids[offset]);
    }

    for (offset = 0; offset < sizeof(OSAL_set->object_ids); ++offset)
    {
        objids = OSAL_set->object_ids[offset];
        fprintf(log, "offset %zu start objids=0x%02X\n", offset, (unsigned)objids);
        bit = 0;
        while (objids != 0)
        {
            id = OSAL_INDEX_C((offset * 8) + bit);
            if ((objids & 0x01) != 0 && id < OS_MAX_NUM_OPEN_FILES)
            {
                osfd = OS_impl_filehandle_table[id].fd;

                /* Preserve original logic: only call FD_ISSET for non-negative file descriptors. */
                int is_set = 0;
                if (osfd >= 0)
                {
                    is_set = FD_ISSET(osfd, OS_set);
                }

                fprintf(log, "  offset=%zu bit=%zu id=%u osfd=%d is_set=%d\n",
                        offset, bit, (unsigned)id, osfd, is_set);

                if (osfd < 0 || !is_set)
                {
                    /* Clear the bit in the output set, as per original behavior. */
                    OSAL_set->object_ids[offset] &= ~(1 << bit);
                    fprintf(log, "    -> cleared bit %zu at offset %zu, new value=0x%02X\n",
                            bit, offset, (unsigned)OSAL_set->object_ids[offset]);
                }
                else
                {
                    fprintf(log, "    -> kept bit %zu at offset %zu\n", bit, offset);
                }
            }
            ++bit;
            objids >>= 1;
        }
    }

    /* Dump final object_ids bytes for traceability */
    for (offset = 0; offset < sizeof(OSAL_set->object_ids); ++offset)
    {
        fprintf(log, "final object_ids[%zu]=0x%02X\n", offset, (unsigned)OSAL_set->object_ids[offset]);
    }

    fprintf(log, "EXIT OS_FdSet_ConvertOut_Impl\n\n");

    if (log != stderr)
    {
        fclose(log);
    }
}
