int32 OS_FileOpenCheck(const char *Filename)
{
    int32                        return_code;
    OS_object_iter_t             iter;
    OS_stream_internal_record_t *stream;
    FILE *log = fopen("/tmp/logs/1275.txt", "a");

    if (log)
    {
        fprintf(log, "OS_FileOpenCheck ENTER: Filename_ptr=%p", (void *)Filename);
        if (Filename)
        {
            /* Print as much as is reasonably safe for diagnostics */
            fprintf(log, " Filename='%s'", Filename);
        }
        fprintf(log, "\n");
        fflush(log);
    }

    /* Check parameters */
    OS_CHECK_POINTER(Filename);

    return_code = OS_ERROR;

    OS_ObjectIdIterateActive(LOCAL_OBJID_TYPE, &iter);

    while (OS_ObjectIdIteratorGetNext(&iter))
    {
        stream = OS_OBJECT_TABLE_GET(OS_stream_table, iter.token);

        if (log)
        {
            /* Log iterator token and stream pointer. Use unsigned long long to be permissive about integer width. */
            fprintf(log, "Iterator next: token=%llu, stream_ptr=%p\n",
                    (unsigned long long)iter.token, (void *)stream);
            if (stream != NULL)
            {
                fprintf(log, "  stream->stream_name_ptr=%p", (void *)stream->stream_name);
                if (stream->stream_name != NULL)
                {
                    fprintf(log, " ('%s')", stream->stream_name);
                }
                fprintf(log, ", socket_domain=%d\n", (int)stream->socket_domain);
            }
            else
            {
                fprintf(log, "  stream is NULL\n");
            }
            fflush(log);
        }

        if (stream == NULL)
        {
            /* Preserve original behavior - just continue scanning */
            continue;
        }

        /* Safely compare names only when both are non-NULL */
        int cmp_result = 1; /* non-zero means not equal by default */
        if (stream->stream_name != NULL && Filename != NULL)
        {
            cmp_result = strcmp(stream->stream_name, Filename);
            if (log)
            {
                fprintf(log, "  strcmp(stream->stream_name, Filename) = %d\n", cmp_result);
                fflush(log);
            }
        }
        else if (log)
        {
            fprintf(log, "  Skipping strcmp because one of the strings is NULL (stream_name=%p, Filename=%p)\n",
                    (void *)stream->stream_name, (void *)Filename);
            fflush(log);
        }

        if (stream->socket_domain == OS_SocketDomain_INVALID && (cmp_result == 0))
        {
            if (log)
            {
                fprintf(log, "  Match found: token=%llu, stream_ptr=%p -> returning OS_SUCCESS\n",
                        (unsigned long long)iter.token, (void *)stream);
                fflush(log);
            }

            return_code = OS_SUCCESS;
            break;
        }
    }

    OS_ObjectIdIteratorDestroy(&iter);

    if (log)
    {
        fprintf(log, "OS_FileOpenCheck EXIT: return_code=%d\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
