int32 OS_FileOpenCheck(const char *Filename)
{
    int32                        return_code;
    OS_object_iter_t             iter;
    OS_stream_internal_record_t *stream;

    /* Open log file to record control flow and state for reproduction */
    FILE *log = fopen("/tmp/logs/1275.txt", "a");
    if (log)
    {
        fprintf(log, "OS_FileOpenCheck ENTER: Filename ptr=%p name='%s'\n", (void *)Filename, Filename ? Filename : "(null)");
        fflush(log);
    }

    /* Check parameters */
    OS_CHECK_POINTER(Filename);

    return_code = OS_ERROR;

    OS_ObjectIdIterateActive(LOCAL_OBJID_TYPE, &iter);

    while (OS_ObjectIdIteratorGetNext(&iter))
    {
        stream = OS_OBJECT_TABLE_GET(OS_stream_table, iter.token);

        if (log)
        {
            /* Log iterator token and stream state. Use unsigned long long for token to avoid format issues. */
            fprintf(log, "ITER token=%llu stream_ptr=%p socket_domain=%d stream_name='%s'\n",
                    (unsigned long long)iter.token,
                    (void *)stream,
                    stream ? (int)stream->socket_domain : -1,
                    stream ? stream->stream_name : "(null)");
            fflush(log);
        }

        /* Preserve original logic: only consider streams with invalid socket domain and matching name */
        if (stream->socket_domain == OS_SocketDomain_INVALID)
        {
            int cmp = strcmp(stream->stream_name, Filename);

            if (log)
            {
                fprintf(log, "  strcmp(stream->stream_name, Filename) => %d\n", cmp);
                fflush(log);
            }

            if (cmp == 0)
            {
                if (log)
                {
                    fprintf(log, "  MATCH found token=%llu (setting return_code=OS_SUCCESS)\n", (unsigned long long)iter.token);
                    fflush(log);
                }

                return_code = OS_SUCCESS;
                break;
            }
        }
    }

    OS_ObjectIdIteratorDestroy(&iter);

    if (log)
    {
        fprintf(log, "OS_FileOpenCheck EXIT return_code=%d\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
