int32 OS_FileOpen_Impl(const OS_object_token_t *token, const char *local_path, int32 flags, int32 access_mode)
{
    int                             os_perm;
    int                             os_mode;
    OS_impl_file_internal_record_t *impl;
    FILE                          *log = NULL;

    /* Open an append-only log for probe information. */
    log = fopen("/tmp/logs/1938.txt", "a");
    if (log)
    {
        fprintf(log, "OS_FileOpen_Impl ENTER: token=%p local_path=%s flags=0x%08x access_mode=%d\n",
                (void *)token, local_path ? local_path : "(null)", (unsigned)flags, (int)access_mode);
        fflush(log);
    }

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    if (log)
    {
        fprintf(log, "Resolved impl pointer=%p\n", (void *)impl);
        fflush(log);
    }

    /*
    ** Check for a valid access mode
    ** For creating a file, OS_READ_ONLY does not make sense
    */
    switch (access_mode)
    {
        case OS_WRITE_ONLY:
            os_perm = O_WRONLY;
            if (log) { fprintf(log, "access_mode -> O_WRONLY (os_perm=0x%08x)\n", os_perm); fflush(log); }
            break;
        case OS_READ_ONLY:
            os_perm = O_RDONLY;
            if (log) { fprintf(log, "access_mode -> O_RDONLY (os_perm=0x%08x)\n", os_perm); fflush(log); }
            break;
        case OS_READ_WRITE:
            os_perm = O_RDWR;
            if (log) { fprintf(log, "access_mode -> O_RDWR (os_perm=0x%08x)\n", os_perm); fflush(log); }
            break;
        default:
            if (log)
            {
                fprintf(log, "Invalid access_mode=%d, returning OS_ERROR\n", (int)access_mode);
                fclose(log);
            }
            return OS_ERROR;
    }

    if (flags & OS_FILE_FLAG_CREATE)
    {
        os_perm |= O_CREAT;
        if (log) { fprintf(log, "FLAG_CREATE set -> os_perm now=0x%08x\n", os_perm); fflush(log); }
    }
    if (flags & OS_FILE_FLAG_TRUNCATE)
    {
        os_perm |= O_TRUNC;
        if (log) { fprintf(log, "FLAG_TRUNCATE set -> os_perm now=0x%08x\n", os_perm); fflush(log); }
    }

    os_perm |= OS_IMPL_REGULAR_FILE_FLAGS;
    if (log) { fprintf(log, "Applied OS_IMPL_REGULAR_FILE_FLAGS -> os_perm=0x%08x\n", os_perm); fflush(log); }

    os_mode = S_IRUSR | S_IWUSR | S_IRGRP | S_IWGRP | S_IROTH | S_IWOTH;
    if (log) { fprintf(log, "os_mode set to 0%o\n", os_mode); fflush(log); }

    impl->fd = open(local_path, os_perm, os_mode);

    if (impl->fd < 0)
    {
        OS_DEBUG("open(%s): %s\n", local_path, strerror(errno));
        if (log)
        {
            fprintf(log, "open(%s) failed: errno=%d (%s) -> returning OS_ERROR\n",
                    local_path ? local_path : "(null)", errno, strerror(errno));
            fclose(log);
        }
        return OS_ERROR;
    }

    if (log)
    {
        fprintf(log, "open(%s) succeeded -> fd=%d\n", local_path ? local_path : "(null)", impl->fd);
        fflush(log);
    }

    /*
     * If the flags included O_NONBLOCK, then
     * enable the "select" call on this handle.
     */
    /* Log previous selectable value if available, then set the new value. */
    if (log)
    {
        /* Attempt to log the prior value; if struct uninitialized this may be garbage but it's useful for probing. */
        fprintf(log, "Before assignment: impl->selectable (raw) = %d\n", (int)impl->selectable);
        fflush(log);
    }

    impl->selectable = ((os_perm & O_NONBLOCK) != 0);

    if (log)
    {
        fprintf(log, "After assignment: impl->selectable = %d (os_perm=0x%08x O_NONBLOCK=%d)\n",
                (int)impl->selectable, os_perm, (int)((os_perm & O_NONBLOCK) != 0));
        fprintf(log, "OS_FileOpen_Impl EXIT: returning OS_SUCCESS\n");
        fclose(log);
    }

    return OS_SUCCESS;
}
