int32 OS_FileOpen_Impl(const OS_object_token_t *token, const char *local_path, int32 flags, int32 access_mode)
{
    int                             os_perm;
    int                             os_mode;
    OS_impl_file_internal_record_t *impl;

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    /*
    ** Log entry to help reproduce and demonstrate the potential dead-code scenario.
    ** The log file is appended to so repeated runs preserve history.
    */
    {
        FILE *logf = fopen("/tmp/logs/1938.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "ENTER: OS_FileOpen_Impl token=%p local_path=%s flags=0x%08x access_mode=%d impl=%p\n",
                    (void *)token, local_path ? local_path : "(null)", (unsigned)flags, (int)access_mode, (void *)impl);
            fclose(logf);
        }
        else
        {
            /* If logging to file fails, still continue silently; original behaviour must be preserved. */
        }
    }

    /*
    ** Check for a valid access mode
    ** For creating a file, OS_READ_ONLY does not make sense
    */
    switch (access_mode)
    {
        case OS_WRITE_ONLY:
            os_perm = O_WRONLY;
            break;
        case OS_READ_ONLY:
            os_perm = O_RDONLY;
            break;
        case OS_READ_WRITE:
            os_perm = O_RDWR;
            break;
        default:
            {
                FILE *logf = fopen("/tmp/logs/1938.txt", "a");
                if (logf != NULL)
                {
                    fprintf(logf, "EXIT: invalid access_mode=%d -> return OS_ERROR\n", (int)access_mode);
                    fclose(logf);
                }
            }
            return OS_ERROR;
    }

    if (flags & OS_FILE_FLAG_CREATE)
    {
        os_perm |= O_CREAT;
    }
    if (flags & OS_FILE_FLAG_TRUNCATE)
    {
        os_perm |= O_TRUNC;
    }

    /* Probe the state immediately before applying OS_IMPL_REGULAR_FILE_FLAGS */
    {
        int before_or = os_perm;
        int after_or = before_or | (int)OS_IMPL_REGULAR_FILE_FLAGS;

        FILE *logf = fopen("/tmp/logs/1938.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "PROBE: os_perm before=0x%08x OS_IMPL_REGULAR_FILE_FLAGS=0x%08x after_expected=0x%08x\n",
                    (unsigned)before_or, (unsigned)((int)OS_IMPL_REGULAR_FILE_FLAGS), (unsigned)after_or);

            if (after_or == before_or)
            {
                fprintf(logf, "PROBE: NOTE: OR operation is redundant (no change). This demonstrates the potential dead-code scenario for applying OS_IMPL_REGULAR_FILE_FLAGS.\n");
            }
            else
            {
                fprintf(logf, "PROBE: OR operation changes os_perm; not redundant at runtime.\n");
            }
            fclose(logf);
        }
    }

    os_perm |= OS_IMPL_REGULAR_FILE_FLAGS;

    os_mode = S_IRUSR | S_IWUSR | S_IRGRP | S_IWGRP | S_IROTH | S_IWOTH;

    impl->fd = open(local_path, os_perm, os_mode);

    if (impl->fd < 0)
    {
        /* Log the error for reproducibility and to capture errno state */
        {
            FILE *logf = fopen("/tmp/logs/1938.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "ERROR: open(%s) failed: errno=%d (%s) os_perm=0x%08x os_mode=0%o impl=%p\n",
                        local_path ? local_path : "(null)", errno, strerror(errno), (unsigned)os_perm, (unsigned)os_mode, (void *)impl);
                fclose(logf);
            }
        }

        OS_DEBUG("open(%s): %s\n", local_path, strerror(errno));
        return OS_ERROR;
    }

    /*
     * If the flags included O_NONBLOCK, then
     * enable the "select" call on this handle.
     */
    impl->selectable = ((os_perm & O_NONBLOCK) != 0);

    /* Log successful open and selectability for traceability */
    {
        FILE *logf = fopen("/tmp/logs/1938.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "EXIT: open succeeded fd=%d selectable=%d os_perm=0x%08x\n",
                    impl->fd, (int)impl->selectable, (unsigned)os_perm);
            fclose(logf);
        }
    }

    return OS_SUCCESS;
}
