int32 OS_FileStat_Impl(const char *local_path, os_fstat_t *FileStats)
{
    struct stat     st;
    mode_t          readbits;
    mode_t          writebits;
    mode_t          execbits;
    struct timespec filetime;

    /* Logging probe: record entry and input state for debugging potential buffer-overflow scenarios */
    {
        FILE *log = fopen("/tmp/logs/3063.txt", "a");
        if (log != NULL)
        {
            if (local_path != NULL)
            {
                /* record pointer, length, and content (may be long) */
                size_t path_len = strlen(local_path);
                fprintf(log, "OS_FileStat_Impl ENTRY: local_path=%p length=%zu\n", (void *)local_path, path_len);
                /* Print the path; do not alter it. This helps reproduce cases where a too-long path
                   may have been copied into a fixed-size buffer by an earlier function. */
                fprintf(log, "OS_FileStat_Impl PATH: %s\n", local_path);
            }
            else
            {
                fprintf(log, "OS_FileStat_Impl ENTRY: local_path=NULL\n");
            }
            fprintf(log, "OS_FileStat_Impl FileStats ptr=%p\n", (void *)FileStats);
            fclose(log);
        }
    }

    if (stat(local_path, &st) < 0)
    {
        /* log the failure and errno for diagnostic evidence */
        FILE *log = fopen("/tmp/logs/3063.txt", "a");
        if (log != NULL)
        {
            int err = errno;
            fprintf(log, "OS_FileStat_Impl stat FAILED: local_path=%p errno=%d (%s)\n", (void *)local_path, err, strerror(err));
            fclose(log);
        }
        return OS_ERROR;
    }

    FileStats->FileSize = st.st_size;

    /*
     * NOTE: Traditional timestamps are only a whole number of seconds (time_t)
     * POSIX.1-2008 expands this to have a full "struct timespec" with nanosecond
     * resolution.
     *
     * GLIBC (and likely other C libraries that use similar feature selection)
     * will expose this value based on _POSIX_C_SOURCE or _XOPEN_SOURCE minimum
     * values.  Otherwise this just falls back to standard 1-second resolution
     * available via the "st_mtime" member.
     */
#if (_POSIX_C_SOURCE >= 200809L) || (_XOPEN_SOURCE >= 700)
    /*
     * Better - use the full resolution (seconds + nanoseconds) as specified in POSIX.1-2008
     */
    filetime = st.st_mtim;
#else
    /*
     * Fallback - every POSIX-compliant implementation must expose "st_mtime" field.
     */
    filetime.tv_sec  = st.st_mtime;
    filetime.tv_nsec = 0;
#endif

    FileStats->FileTime = OS_TimeAssembleFromNanoseconds(filetime.tv_sec, filetime.tv_nsec);

    /* note that the "fst_mode" member is already zeroed by the caller */
    if (S_ISDIR(st.st_mode))
    {
        FileStats->FileModeBits |= OS_FILESTAT_MODE_DIR;
    }

    /* always check world bits */
    readbits  = S_IROTH;
    writebits = S_IWOTH;
    execbits  = S_IXOTH;

    if (OS_IMPL_SELF_EUID == st.st_uid)
    {
        /* we own the file so use user bits for simplified perms */
        readbits |= S_IRUSR;
        writebits |= S_IWUSR;
        execbits |= S_IXUSR;
    }

    if (OS_IMPL_SELF_EGID == st.st_gid)
    {
        /* our group owns the file so use group bits for simplified perms */
        readbits |= S_IRGRP;
        writebits |= S_IWGRP;
        execbits |= S_IXGRP;
    }

    if (st.st_mode & readbits)
    {
        FileStats->FileModeBits |= OS_FILESTAT_MODE_READ;
    }
    if (st.st_mode & writebits)
    {
        FileStats->FileModeBits |= OS_FILESTAT_MODE_WRITE;
    }
    if (st.st_mode & execbits)
    {
        FileStats->FileModeBits |= OS_FILESTAT_MODE_EXEC;
    }

    /* Log resulting FileStats for evidence */
    {
        FILE *log = fopen("/tmp/logs/3063.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_FileStat_Impl EXIT: FileSize=%lld FileTime=%llu FileModeBits=0x%08x\n",
                    (long long)FileStats->FileSize,
                    (unsigned long long)FileStats->FileTime,
                    (unsigned int)FileStats->FileModeBits);
            /* Also log some of the stat struct fields that are helpful for debugging */
            fprintf(log, "OS_FileStat_Impl stat info: st_mode=0%o st_uid=%u st_gid=%u\n",
                    (unsigned int)st.st_mode,
                    (unsigned int)st.st_uid,
                    (unsigned int)st.st_gid);
            fclose(log);
        }
    }

    return OS_SUCCESS;
}
