int32 OS_FileSysStatVolume(const char *name, OS_statvfs_t *statbuf)
{
    int32             return_code;
    OS_object_token_t token;

    /* Minimal entry probe so the function is known to have been entered */
    {
        FILE *log_fp = fopen("/tmp/logs/1630.txt", "a");
        if (log_fp != NULL)
        {
            fprintf(log_fp, "ENTER: OS_FileSysStatVolume name=%p statbuf=%p\n", (void *)name, (void *)statbuf);
            fclose(log_fp);
        }
    }

    /* Check parameters */
    OS_CHECK_PATHNAME(name);
    OS_CHECK_POINTER(statbuf);

    /* Log after parameter checks (name is now assumed valid) */
    {
        FILE *log_fp = fopen("/tmp/logs/1630.txt", "a");
        if (log_fp != NULL)
        {
            fprintf(log_fp, "PARAMS_OK: name=\"%s\" statbuf=%p\n", name, (void *)statbuf);
            fclose(log_fp);
        }
    }

    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)name, &token);

    /* Log the outcome of the lookup */
    {
        FILE *log_fp = fopen("/tmp/logs/1630.txt", "a");
        if (log_fp != NULL)
        {
            fprintf(log_fp, "OS_ObjectIdGetBySearch returned %d token=%p\n", (int)return_code, (void *)&token);
            fclose(log_fp);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        /* Log before calling the implementation */
        {
            FILE *log_fp = fopen("/tmp/logs/1630.txt", "a");
            if (log_fp != NULL)
            {
                fprintf(log_fp, "CALL: OS_FileSysStatVolume_Impl token=%p statbuf=%p\n", (void *)&token, (void *)statbuf);
                fclose(log_fp);
            }
        }

        return_code = OS_FileSysStatVolume_Impl(&token, statbuf);

        /* Log result from implementation */
        {
            FILE *log_fp = fopen("/tmp/logs/1630.txt", "a");
            if (log_fp != NULL)
            {
                fprintf(log_fp, "RETURN_FROM_Impl: return_code=%d statbuf=%p\n", (int)return_code, (void *)statbuf);
                fclose(log_fp);
            }
        }

        OS_ObjectIdRelease(&token);

        /* Log that the token was released */
        {
            FILE *log_fp = fopen("/tmp/logs/1630.txt", "a");
            if (log_fp != NULL)
            {
                fprintf(log_fp, "RELEASED_TOKEN: token=%p\n", (void *)&token);
                fclose(log_fp);
            }
        }
    }
    else
    {
        /* Log why we are not able to proceed (error path) */
        FILE *log_fp = fopen("/tmp/logs/1630.txt", "a");
        if (log_fp != NULL)
        {
            fprintf(log_fp, "ERROR_PATH: OS_ObjectIdGetBySearch failed with %d for name=\"%s\"\n", (int)return_code, (name != NULL) ? name : "(null)");
            fclose(log_fp);
        }
    }

    /* Final return log and return */
    {
        FILE *log_fp = fopen("/tmp/logs/1630.txt", "a");
        if (log_fp != NULL)
        {
            fprintf(log_fp, "EXIT: OS_FileSysStatVolume returning %d\n", (int)return_code);
            fclose(log_fp);
        }
    }

    return return_code;
}
