int32 OS_FileSysStatVolume(const char *name, OS_statvfs_t *statbuf)
{
    int32             return_code;
    OS_object_token_t token;

    /* Minimal runtime logging to capture control flow/state for debugging. */
    {
        FILE *log = fopen("/tmp/logs/1630.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "ENTER: OS_FileSysStatVolume\n");
            fprintf(log, "  name = %s\n", (name != NULL) ? name : "(null)");
            fprintf(log, "  statbuf = %p\n", (void *)statbuf);
            fflush(log);
            fclose(log);
        }
    }

    /* Check parameters */
    OS_CHECK_PATHNAME(name);
    OS_CHECK_POINTER(statbuf);

    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)name, &token);

    {
        FILE *log = fopen("/tmp/logs/1630.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "After OS_ObjectIdGetBySearch: return_code=%ld\n", (long)return_code);
            fprintf(log, "  token addr = %p\n", (void *)&token);
            fflush(log);
            fclose(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        {
            FILE *log = fopen("/tmp/logs/1630.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "Calling OS_FileSysStatVolume_Impl\n");
                fflush(log);
                fclose(log);
            }
        }

        return_code = OS_FileSysStatVolume_Impl(&token, statbuf);

        {
            FILE *log = fopen("/tmp/logs/1630.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "After OS_FileSysStatVolume_Impl: return_code=%ld\n", (long)return_code);
                fflush(log);
                fclose(log);
            }
        }

        OS_ObjectIdRelease(&token);

        {
            FILE *log = fopen("/tmp/logs/1630.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "Released token and returning from successful path\n");
                fflush(log);
                fclose(log);
            }
        }
    }
    else
    {
        /* Log the failure path as well */
        FILE *log = fopen("/tmp/logs/1630.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "Exiting OS_FileSysStatVolume with error code: %ld\n", (long)return_code);
            fflush(log);
            fclose(log);
        }
    }

    return return_code;
}
